		["Explosive Orbs || Mythic+"] = {
			["iconSource"] = 0,
			["xOffset"] = 251.000244140625,
			["customText"] = "function()\n    return #aura_env.orbs\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
			["yOffset"] = 140.5006713867188,
			["anchorPoint"] = "CENTER",
			["cooldownSwipe"] = true,
			["customTextUpdate"] = "update",
			["url"] = "https://wago.io/explosiveorbs/26",
			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "local enableGlow = true -- glow\nlocal enableIcon = true -- show icon\nlocal enableMarks = false -- assign marks\nlocal playSound = false -- play sound whenever new orb spawns\nlocal sound = \"Sonar\" -- Available: Sonar,Bam,AirHorn,BikeHorn,Wilhelm,Phone,Bite\nlocal customGlow = false -- Use AutoCast Shine glow from Deezo\n\n-- custom glow options --\nlocal red,green,blue = 1,0.9,0 -- color of glow\nlocal parcticleGrps = 8 --  number of particle groups (each groups contains 4 particles)\nlocal glowPeriod = 4 -- time for biggest particle to travel around region\nlocal particleScale = 1 -- scale of particles\nlocal rainbowGlow = false -- RAINBOWS!!! only for custom\n\n-- DO NOT TOUCH --\nlocal idKey = 'explosiveGlow' \nlocal tidyplatesHeight = 8\naura_env.test = false -- change to make all nameplates glow for testing (also remember load condition if you are testing outside of m+ with explosive affix)\naura_env.nameplates = {}\n-- GLOW --\nlocal GetFrame = function(unit)\n    local nameplate = C_NamePlate.GetNamePlateForUnit(unit)\n    if not nameplate then return end\n    if nameplate.unitFrame and nameplate.unitFrame.HealthBar then\n        -- elvui\n        return nameplate.unitFrame.HealthBar\n    elseif nameplate.kui then\n        -- kui\n        return nameplate.kui.HealthBar\n    elseif nameplate.extended then\n        -- tidyplates\n        nameplate.extended.visual.healthbar:SetHeight(tidyplatesHeight)\n        return nameplate.extended.visual.healthbar\n    elseif nameplate.TPFrame then\n        -- tidyplates: threat plates\n        return nameplate.TPFrame.visual.healthbar\n    elseif nameplate.ouf then\n        -- bdNameplates\n        return nameplate.ouf.Health\n    elseif nameplate.UnitFrame then\n        -- default\n        return nameplate.UnitFrame.healthBar\n    else\n        return nameplate\n    end\nend\n-- RAINBOW --\nlocal function hsvToRgb(h, s, v)\n    local r, g, b\n    \n    local i = math.floor(h * 6);\n    local f = h * 6 - i;\n    local p = v * (1 - s);\n    local q = v * (1 - f * s);\n    local t = v * (1 - (1 - f) * s);\n    \n    i = i % 6\n    \n    if i == 0 then r, g, b = v, t, p\n    elseif i == 1 then r, g, b = q, v, p\n    elseif i == 2 then r, g, b = p, v, t\n    elseif i == 3 then r, g, b = p, q, v\n    elseif i == 4 then r, g, b = t, p, v\n    elseif i == 5 then r, g, b = v, p, q\n    end\n    \n    return r, g, b\nend\n-- From WeakAuras.lua --\nlocal colorElapsed = 0;\nlocal colorDelay = 0.1;\nlocal aura_env = aura_env\nlocal r, g, b = 0.8, 0, 1;\nlocal hue = 0\nlocal function RainbowFunc(self,elaps)\n    colorElapsed = colorElapsed + elaps;\n    if(colorElapsed > colorDelay) then\n        colorElapsed = colorElapsed - colorDelay;\n        hue = hue >= 1 and 0 or hue + 0.04\n        r, g, b = hsvToRgb(hue,1,1)\n    end\n    for _, nameplate in pairs(self.frames) do\n        aura_env.region.CustomGlowStart(nameplate,r, g, b,parcticleGrps,glowPeriod,particleScale)\n    end\nend\nif rainbowGlow and not aura_env.region.rainbowGlow then\n    -- Frame --\n    aura_env.region.rainbowGlow = true\n    aura_env.region.rainbowFrame = CreateFrame(\"Frame\")\n    aura_env.region.rainbowFrame.frames = {}\n    function aura_env.region.rainbowFrame:RegisterNameplate(unit,nameplate)\n        self.frames[unit] = nameplate\n    end\n    function aura_env.region.rainbowFrame:UnregisterNameplate(unit)\n        self.frames[unit] = nil\n    end\n    function aura_env.region.rainbowFrame:ClearNameplates()\n        for i in pairs(self.frames) do\n            self.frames[i] = nil\n        end\n    end\n    aura_env.region.rainbowFrame:SetScript(\"OnUpdate\",RainbowFunc)\nelseif rainbowGlow and aura_env.region.rainbowGlow then\n    aura_env.region.rainbowFrame:SetScript(\"OnUpdate\",RainbowFunc)\nend\n\naura_env.GlowPlate = function(unit,show)\n    local glowHideFunc = customGlow and aura_env.region.CustomGlowStop or ActionButton_HideOverlayGlow\n    if enableGlow and show then\n        local nameplate = GetFrame(unit)\n        if not nameplate then return end\n        if customGlow then\n            if rainbowGlow then\n                nameplate[idKey] = true\n                aura_env.region.rainbowFrame:RegisterNameplate(unit,nameplate)\n            else\n                nameplate[idKey] = true\n                aura_env.region.CustomGlowStart(nameplate,red,green,blue,parcticleGrps,glowPeriod,particleScale)\n            end\n        else\n            nameplate[idKey] = true\n            ActionButton_ShowOverlayGlow(nameplate)\n        end\n        aura_env.nameplates[unit] = nameplate\n    elseif not show and aura_env.nameplates[unit] then\n        if rainbowGlow and customGlow then\n            aura_env.region.rainbowFrame:UnregisterNameplate(unit)\n        end\n        aura_env.nameplates[unit][idKey] = false\n        glowHideFunc(aura_env.nameplates[unit])\n        aura_env.nameplates[unit] = nil\n    end\nend\n\naura_env.CheckPlateGlow = function(unit)\n    -- checks if nameplate is glowing and if it shouldnt be\n    local nameplate = GetFrame(unit)\n    if nameplate and nameplate.__LBGoverlay and nameplate[idKey] then\n        nameplate[idKey] = false\n        ActionButton_HideOverlayGlow(nameplate)\n    elseif nameplate and nameplate.ACShine and nameplate[idKey] then\n        nameplate[idKey] = false\n        aura_env.region.CustomGlowStop(nameplate)\n    end\nend\n\n-- MARKING --\nlocal availableMarks = {true,true,true,true,true,true,true,true}\n\nlocal function GetAvailableMark()\n    for markId,avail in ipairs(availableMarks) do\n        if avail then\n            availableMarks[markId] = false\n            return markId\n        end\n    end\nend\n\naura_env.ShowMarks = function()\n    if not enableMarks then return end\n    local indx = 1\n    while indx <= 8 do\n        if aura_env.orbs[indx] and not aura_env.orbs[indx].marked then\n            local markId = GetAvailableMark()\n            --print('Setting mark',markId,'to unit:',aura_env.orbs[indx].unit)\n            SetRaidTarget(aura_env.orbs[indx].unit,markId)\n            aura_env.orbs[indx].marked = markId\n        elseif not aura_env.orbs[indx] then\n            break\n        end\n        indx = indx + 1\n    end\nend\n\n-- SOUND --\nlocal sounds = {\n    Sonar = [[Interface\\AddOns\\WeakAuras\\PowerAurasMedia\\Sounds\\Sonar.ogg]],\n    Bam = [[Interface\\AddOns\\WeakAuras\\PowerAurasMedia\\Sounds\\bam.ogg]],\n    AirHorn = [[Interface\\AddOns\\WeakAuras\\Media\\Sounds\\AirHorn.ogg]],\n    BikeHorn = [[Interface\\AddOns\\WeakAuras\\Media\\Sounds\\BikeHorn.ogg]],\n    Wilhelm = [[Interface\\AddOns\\WeakAuras\\PowerAurasMedia\\Sounds\\wilhelm.ogg]],\n    Phone = [[Interface\\AddOns\\WeakAuras\\PowerAurasMedia\\Sounds\\phone.ogg]],\n    Bite = [[Interface\\AddOns\\WeakAuras\\PowerAurasMedia\\Sounds\\BITE.ogg]]\n    \n}\nlocal lastSound = 0\naura_env.PlaySound = function()\n    if playSound and GetTime() - lastSound > 1 then\n        lastSound = GetTime()\n        PlaySoundFile(sounds[sound] or \"\",\"Master\")\n    end\nend\n\n-- ICON --\nlocal r,g,b,a = aura_env.region:GetColor()\naura_env.ShowIcon = function()\n    if enableIcon and #aura_env.orbs > 0 then\n        aura_env.region:SetAlpha(1)\n        aura_env.region:Color(r,g,b,a)\n    else\n        aura_env.region:SetAlpha(0)\n        aura_env.region:Color(r,g,b,0)\n    end\nend\n\n\n-- REMOVAL --\naura_env.RemoveOrb = function(guid)\n    for indx,orbInfo in ipairs(aura_env.orbs) do\n        if orbInfo.guid == guid then\n            if orbInfo.marked then\n                availableMarks[orbInfo.marked] = true\n            end\n            table.remove(aura_env.orbs,indx)\n            break\n        end\n    end\nend\n\naura_env.GetNPCId = function(guid)\n    local type, zero, server_id, instance_id, zone_uid, npc_id, spawn_uid = strsplit(\"-\",guid)\n    return tonumber(npc_id)\nend\naura_env.orbs = {}\naura_env.npcID = { -- possible to add more\n    [120651] = true, -- fel explosives\n    -- [91783] = true, -- test\n    -- [31146] = true, -- testingdummy\n    -- [92168] = true, -- testingdummies\n    -- [92166] = true, -- testingdummies\n}\n\nlocal function InitializeCustomGlow()\n    -- https://wago.io/HJkDQqQXQ --\n    -- AutoCast Shine by Deezo --\n    local AutoCastShineParentFrame = CreateFrame(\"Frame\")\n    \n    local AutoCastShineTexPool = CreateTexturePool(AutoCastShineParentFrame ,\"ARTWORK\",7)\n    local FramePoolResetter = function(framePool,frame)\n        frame:SetScript(\"OnUpdate\",nil)\n        local parent = frame:GetParent()\n        if parent.ACShine then\n            parent.ACShine = nil\n            --frame:SetParent(AutoCastShineParentFrame)\n        end\n        if frame.sparkles then\n            for index, sparkle in pairs(frame.sparkles) do\n                AutoCastShineTexPool:Release(sparkle)\n            end\n        end\n        frame.sparkles = {}\n        frame.timers = {0,0,0,0}\n        frame:Hide()\n        frame:ClearAllPoints()\n    end\n    local AutoCastShineFramePool = CreateFramePool(\"Frame\",AutoCastShineParentFrame,nil,FramePoolResetter)\n    \n    \n    local ACShine_OnUpdate = function(self,elapsed)\n        local width,height = self:GetSize()\n        local perimeter = 2*(width+height)\n        local bottomlim = height*2+width\n        local rightlim = height+width\n        local space = perimeter/self.N\n        for k,timer in pairs(self.timers) do\n            self.timers[k] = timer+elapsed\n            if timer > self.period*k then\n                timer = timer%(self.period*k)\n            end\n            for i=1,self.N do\n                local pN = i+self.N*(k-1)\n                local position = (space*i+perimeter*timer/(self.period*k))%perimeter\n                if position>bottomlim then\n                    self.sparkles[pN]: SetPoint(\"CENTER\",self,\"BOTTOMRIGHT\",-position+bottomlim,0)\n                elseif position>rightlim then\n                    self.sparkles[pN]: SetPoint(\"CENTER\",self,\"TOPRIGHT\",0,-position+rightlim)\n                elseif position>height then\n                    self.sparkles[pN]: SetPoint(\"CENTER\",self,\"TOPLEFT\",position-height,0)\n                else\n                    self.sparkles[pN]: SetPoint(\"CENTER\",self,\"BOTTOMLEFT\",0,position)\n                end\n            end\n        end\n    end\n    \n    aura_env.region.CustomGlowStart = function(region,r,g,b,N,period,scale)\n        r = r or 0.95\n        g = g or 0.95\n        b = b or 0.32\n        if N then\n            if not(N>0) then\n                N = 4\n            end\n        else\n            N = 4\n        end\n        \n        scale = scale or 1\n        \n        if not region.ACShine then\n            local frame,new =  AutoCastShineFramePool:Acquire()\n            if new then\n                frame.sparkles = {}\n                frame.timers = {0,0,0,0}\n            end\n            region.ACShine = frame\n        end\n        local f = region.ACShine\n        f:SetParent(region)\n        f:SetFrameLevel(region:GetFrameLevel()+4)\n        f:SetAllPoints(region)\n        f:Show()\n        \n        local width,height = f:GetSize();\n        local space =  2*(width+height)/N\n        local sizes ={13,10,7,4}\n        \n        if period then\n            if not(period>0.4) then\n                period = 8\n            end\n        else\n            period = 8\n        end\n        \n        \n        for k,size in pairs(sizes) do\n            for i = 1,N do\n                local pN = i+(k-1)*N\n                if not f.sparkles[pN] then\n                    local sparkle,new = AutoCastShineTexPool:Acquire()\n                    if new then\n                        sparkle:SetTexture(\"Interface\\\\Challenges\\\\challenges-metalglow\")\n                        sparkle:SetDesaturated(true)\n                        sparkle:SetBlendMode(\"ADD\")\n                    end\n                    f.sparkles[pN] = sparkle\n                    f.sparkles[pN]:SetParent(f)\n                end\n                f.sparkles[pN]: SetSize(size*scale,size*scale)\n                f.sparkles[pN]: SetVertexColor(r,g,b)\n                f.sparkles[pN]:Show()\n            end\n        end\n        \n        while #f.sparkles>N*4 do\n            AutoCastShineTexPool:Release(f.sparkles[#f.sparkles])\n            table.remove(f.sparkles)\n        end\n        \n        f.N = N\n        f.period = period\n        f: SetScript(\"OnUpdate\",ACShine_OnUpdate)\n    end\n    \n    aura_env.region.CustomGlowStop = function(region)\n        if not region.ACShine then\n            return false\n        end\n        AutoCastShineFramePool:Release(region.ACShine)\n        return true\n    end\n    \n    \n    hooksecurefunc(WeakAuras, \"OpenOptions\", function()\n            AutoCastShineFramePool:ReleaseAll()\n    end)\n    \nend\n\nif customGlow and not aura_env.region.customGlowInit then\n    aura_env.region.customGlowInit = true\n    InitializeCustomGlow()\nend\n\n-- hide default glow on options open\nlocal aura_env = aura_env\nhooksecurefunc(WeakAuras, \"OpenOptions\", function()\n        aura_env.nameplates = {}\n        for i=1,40 do\n            local unit = \"nameplate\"..i\n            --aura_env.CheckPlateGlow(unit)\n        end\n        if aura_env.region.rainbowFrame then\n            aura_env.region.rainbowFrame:ClearNameplates()\n        end\nend)",
