imeMin = (\"0%d\"):format(timeMin)\n    end\n    if timeSec < 10 then\n        timeSec = (\"0%d\"):format(timeSec)\n    end\n    \n    local timeMS  = select(2, strsplit(\".\", (time))) or select(2, strsplit(\".\", (GetTime()-time)))\n    local timeMS100 = math.floor(timeMS/100)\n    local timeMS10 = math.floor((timeMS-(timeMS100*100))/10)\n    local timeMS1 =timeMS-(timeMS100*100)-(timeMS10*10)\n    \n    timeMS = (\".%s%s%s\"):format(timeMS100, timeMS10, timeMS1)\n    timeMS = (aura_env.decimals == 0 and \"\") or (timeMS and string.sub(timeMS, 1, aura_env.decimals+1))\n    return (aura_env.color..\"%s:%s%s|r\"):format(timeMin, timeSec, timeMS)\nend\n\nif not aura_env.region.hideobj then\n    aura_env.region.hideobj = true\n    hooksecurefunc(ObjectiveTrackerFrame, \"Show\", function() \n            if IsInInstance() then ObjectiveTrackerFrame:Hide() \n            end \n    end)\nend\n\naura_env.getoptionstext = function()\n    local count = 120\n    local max = 400\n    local pullC = 10\n    local perc = \"30.00%\"\n    local percC = \"2.50%\"\n    local percC2 = \"32.50%\"\n    local text1, text2, text3, text4 = \"\", \"\", \"\", \"\"\n    local dis = aura_env.config[\"Display\"]\n    if dis == 1 or dis == 2 then\n        text1 = perc\n        if aura_env.pullcount then\n            if aura_env.countD == 1 then\n                text2 = \"(+\"..percC..\")\"\n            else\n                text2 = \"(\"..percC2..\")\"\n            end\n        end\n        -- percent shit\n    end\n    if dis == 1 or dis == 3 then\n        if aura_env.remaining then\n            if aura_env.totalC then\n                text3 = max-count..\"/\"..max\n            else\n                text3 = max-count\n            end\n            if aura_env.pullcount then\n                if aura_env.countD == 1 then\n                    text4 = \"(-\"..pullC..\")\"\n                else\n                    text4 = \"(\"..max-count-pullC..\")\"\n                end\n            end\n        else\n            if aura_env.totalC then\n                text3 = count..\"/\"..max\n            else\n                text3 = count\n            end\n            if aura_env.pullcount then\n                if aura_env.countD == 1 then\n                    text4 = \"(+\"..pullC..\")\"\n                else\n                    text4 = \"(\"..count+pullC..\")\"\n                end\n            end\n        end\n    end\n    return text1, text2, text3, text4\nend\n\n\n\n\n\n\n\n--[[\na.prep = GetTime() + 10\nelseif spellId == 258920 then\n    if a.immototal <= 0 then \n        a.immototal = 80\n    end\nelseif spellId == 178740 then\n    a.immototal = 20]]\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
