						["custom"] = "function(states, event, unit, ...)\n    if not states then return end\n    \n    if not aura_env.isMDTInstalled then\n        return false\n    end\n    \n    -- handle nameplate init after options close\n    if aura_env.config[\"showNameplates\"] and event == \"MDT_MP_INIT\" then\n        for i = 1, 80 do\n            local unit = \"nameplate\"..i\n            if UnitExists(unit) then\n                WeakAuras.ScanEvents(\"MDT_MP_INIT_UNIT\", unit)\n            end\n        end\n    end\n    \n    local update = false\n    aura_env.currentPull = aura_env.currentPull or {}\n    local currentPull = aura_env.currentPull\n    \n    -- init teeming state\n    if event == \"WORLD_STATE_TIMER_START\" then\n        aura_env.updateTeeming()\n        if aura_env.config[\"showTextAlways\"] then\n            update = true\n        end\n    end\n    \n    -- update totals through events\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, sub, _, _, _, _, _ , destGUID = unit, ...\n        if sub == \"UNIT_DIED\"\n        and destGUID\n        and currentPull[destGUID]\n        then\n            currentPull[destGUID] = \"DEAD\"\n            update = true\n        end\n    elseif event == \"UNIT_THREAT_LIST_UPDATE\" and InCombatLockdown() then\n        if unit\n        and UnitExists(unit)\n        then\n            local guid = UnitGUID(unit)\n            if guid and not currentPull[guid] then\n                local npc_id = select(6, strsplit(\"-\",guid))\n                if npc_id then\n                    local npcId = tonumber(npc_id)\n                    local value, max, maxteeming, teemingCount = aura_env.GetEnemyForces(npcId)\n                    \n                    if teemingCount and aura_env.teeming then                        \n                        value = teemingCount\n                    end\n                    update = true\n                    if value then\n                        currentPull[guid] = value\n                    else\n                        currentPull[guid] = 0\n                    end                    \n                end\n            end            \n        end\n    elseif event == \"ENCOUNTER_END\"\n    or event == \"PLAYER_REGEN_ENABLED\"\n    or event == \"PLAYER_DEAD\"\n    then\n        aura_env.alreadyPlayedSound = false\n        aura_env.curPercent = aura_env.GetMobCount()\n        for k, v in pairs(currentPull) do\n            currentPull[k] = nil\n        end\n        if not aura_env.config[\"showTextAlways\"] then\n            states[\"PULL_PERCENT\"] = {\n                show = false,\n                changed = true\n            }\n        else\n            update = true\n        end\n    end\n    \n    if event == \"SCENARIO_POI_UPDATE\" then\n        aura_env.curPercent, aura_env.curCount, aura_env.totalCount = aura_env.GetMobCount()\n        update = true\n    end\n    \n    -- update totals now\n    if update then\n        local pullValue, count = 0, 0\n        for guid, value in pairs(currentPull) do \n            if value ~= \"DEAD\" then\n                count = count + 1\n                pullValue = pullValue + value\n            end\n        end\n        \n        if aura_env.config[\"colorPrideful\"] then\n            -- update nameplates for prideful indicator\n            for _, state in pairs(states) do\n                state.changed = true\n                --if currentPull[state.guid] then\n                if state.weight and (state.weight + (aura_env.curPercent % 20)  >= 20) then\n                    state.isPrideful = true\n                else\n                    state.isPrideful = false \n                end\n                --end\n            end\n        end\n        \n        local willSpawnPride = false\n        local curPullPercent = pullValue / (aura_env.totalCount or 1) * 100\n        if aura_env.config[\"colorPridefulPull\"] and ((aura_env.curPercent % 20) + curPullPercent >= 20) then\n            willSpawnPride = aura_env.prideful\n        end\n        \n        local prideSound = false\n        if willSpawnPride and aura_env.config[\"prideSound\"] and not aura_env.alreadyPlayedSound then\n            prideSound = aura_env.prideful\n            aura_env.alreadyPlayedSound = true\n        end\n        \n        \n        local text = \"\"\n        if aura_env.config[\"showText\"] then\n            if aura_env.config[\"showProgressPercent\"] then\n                text = (\"Cur: %0.2f%%\\\\n\"):format(aura_env.curPercent)\n            end\n            if aura_env.config[\"showProgressCount\"] then\n                text = text .. (\"Cur: %d / %d\\\\n\"):format((aura_env.curCount or 0), aura_env.totalCount)\n            end\n            if aura_env.config[\"showCurrentPullPercent\"] then\n                text = text .. (\"Pull: %0.2f%%\\\\n\"):format(pullValue / (aura_env.totalCount or 1) * 100)\n            end\n            if aura_env.config[\"showCurrentPullCount\"] then\n                text = text .. (\"Pull: %d\\\\n\"):format(pullValue)\n            end\n            if aura_env.config[\"showCurrentPullProgressPercent\"] then\n                text = text .. (\"Sum: %0.2f%%\\\\n\"):format((pullValue + (aura_env.curCount or 0)) / (aura_env.totalCount or 1) * 100)\n            end\n            if aura_env.config[\"showCurrentPullProgressCount\"] then\n                text = text .. (\"Sum: %d\\\\n\"):format(pullValue + (aura_env.curCount or 0))\n            end\n            if aura_env.config[\"showMobCount\"] then\n                text = text .. (\"#Mobs: %u\"):format(count)\n            end\n        end\n        \n        states[\"PULL_PERCENT\"] = {\n            show = true,\n            changed = true,\n            text = text,\n            cloneId = \"PULL_PERCENT\",\n            isMaster = true,\n            prideSound = prideSound,\n            isPrideful = willSpawnPride,\n        }\n    end\n    \n    -- handle individual nameplate values\n    if aura_env.config[\"showNameplates\"] and (event == \"NAME_PLATE_UNIT_ADDED\" or event == \"MDT_MP_INIT_UNIT\") then\n        if not unit or not string.match(unit, \"nameplate\") then\n            return true\n        end\n        \n        if not UnitExists(unit) and states[unit] then\n            states[unit].show = false\n            states[unit].changed = true\n            return true\n        end\n        \n        for _, state in pairs(states) do\n            if state.unit == unit then\n                state.show = false\n                state.changed = true\n                state.text = \"\"\n            end\n        end\n        \n        local mobPercentageText = \"\"\n        local weight = nil\n        local npcIdStr = select (6, strsplit (\"-\", UnitGUID(unit)))\n        local npcId = tonumber (npcIdStr or \"0\") or 0\n        local count, max, maxTeeming, teemingCount = aura_env.GetEnemyForces(npcId)\n        \n        \n        if (teemingCount and aura_env.teeming) or not count then\n            count = teemingCount\n        end\n        if (count ~= nil and max ~= nil and maxTeeming ~= nil) then\n            if (aura_env.teeming) then\n                weight = count / maxTeeming\n            else\n                weight = count / max\n            end\n            weight = weight * 100\n        end\n        \n        if (weight and weight > 0) then\n            local guid = UnitGUID(unit)\n            if not aura_env.curPercent then\n                aura_env.curPercent, aura_env.curCount, aura_env.totalCount = aura_env.GetMobCount()\n            end\n            \n            local willSpawnPride = false\n            if aura_env.config[\"colorPrideful\"] and (weight + (aura_env.curPercent % 20) >= 20) then\n                willSpawnPride = aura_env.prideful\n            end\n            \n            local state = {\n                show = true,\n                changed = true,\n                unit = unit,\n                autoHide = true,\n                resort = true,\n                cloneId = unit,\n                text = \"\",\n                guid = guid,\n                weight = weight,\n                isPrideful = willSpawnPride\n            }\n            states[unit] = state\n            \n            if aura_env.config[\"showPercent\"] then\n                mobPercentageText = string.format(\"%.2f%%\", weight);\n            end\n            if aura_env.config[\"showCount\"] then\n                if mobPercentageText ~= \"\" then\n                    mobPercentageText = mobPercentageText .. \" | \"\n                end\n                mobPercentageText = mobPercentageText .. count\n                \n                if aura_env.config[\"showTotal\"] then\n                    if (aura_env.teeming) then\n                        mobPercentageText = mobPercentageText .. \"/\" ..  maxTeeming\n                    else\n                        mobPercentageText = mobPercentageText .. \"/\" ..  max\n                    end\n                end\n            end\n            \n            state.text = \"(\"..mobPercentageText..\")\"\n        end\n    end\n    \n    \n    if aura_env.config[\"showNameplates\"] and event == \"NAME_PLATE_UNIT_REMOVED\" then\n        for _, state in pairs(states) do\n            if state.unit == unit then\n                state.show = false\n                state.changed = true\n            end\n        end\n    end\n    \n    \n    return true\nend",
