						["custom"] = "function(allstates, event, ...)\n    if event == \"ENCOUNTER_START\" then\n        aura_env.GUID2Unit = {}\n        aura_env.affected = {}\n        aura_env.intermissionPriority = {}\n        aura_env.p3Priority = {}\n        aura_env.phase = 1\n        aura_env.count = 0\n        \n        local p3Specific = false -- Specific assignments for p3\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUID2Unit[UnitGUID(unit)] = unit\n        end\n        \n        if (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\")) and _G.VExRT.Note.Text1 then\n            local text = _G.VExRT.Note.Text1\n            \n            -- Set intermission priority\n            local start = false\n            local priority = 1\n            \n            for line in text:gmatch(\"[^\\r\\n]+\") do\n                if start then\n                    if line:match(\"^intermissionEnd\") then break end\n                    \n                    local unit\n                    \n                    for u in WA_IterateGroupMembers() do\n                        local name = UnitName(u)\n                        name = Ambiguate(name, \"none\")\n                        \n                        if line:match(name) then\n                            unit = u\n                            break\n                        end\n                    end\n                    \n                    if unit then\n                        aura_env.intermissionPriority[priority] = unit\n                        priority = priority + 1\n                    end\n                end\n                \n                if line:match(\"^intermissionStart\") then start = true end\n            end\n            \n            -- Set p3 priority\n            start = false\n            priority = 1\n            \n            for line in text:gmatch(\"[^\\r\\n]+\") do\n                if start then\n                    if line:match(\"^p3End\") then break end\n                    \n                    local unit\n                    \n                    for u in WA_IterateGroupMembers() do\n                        local name = UnitName(u)\n                        name = Ambiguate(name, \"none\")\n                        \n                        if line:match(name) then\n                            unit = u\n                            break\n                        end\n                    end\n                    \n                    if unit then\n                        aura_env.p3Priority[priority] = unit\n                        priority = priority + 1\n                    end\n                end\n                \n                if line:match(\"^p3Start\") then\n                    start = true\n                    p3Specific = true\n                end\n            end\n        else\n            message(\"Please turn on your MRT AddOn and enable the note\")\n        end\n        \n        -- If there is no priority list for p3, use the intermission list\n        if not p3Specific then\n            aura_env.p3Priority = aura_env.intermissionPriority\n        end\n    elseif event == \"BDGG_RUNIC_AFFINITY\" and ... then\n        local priorityList = aura_env.phase == 3 and aura_env.p3Priority or aura_env.intermissionPriority\n        local num = 1\n        \n        -- Assign units that were on the MRT note\n        for _, unit in ipairs(priorityList) do\n            if aura_env.affected[unit] then\n                allstates[num] = {\n                    show = true,\n                    changed = true,\n                    index = num,\n                    unit = unit\n                }\n                \n                if UnitIsUnit(\"player\", unit) then\n                    WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, false)\n                end\n                \n                num = num + 1\n                aura_env.affected[unit] = nil\n            end\n        end\n        \n        -- Assign units that were not on the MRT note\n        for unit in pairs(aura_env.affected) do\n            allstates[num] = {\n                show = true,\n                changed = true,\n                index = num,\n                unit = unit\n            }\n            \n            if UnitIsUnit(\"player\", unit) then\n                WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, false)\n            end\n            \n            num = num + 1\n        end\n        \n        return true\n    elseif (event == \"CHAT_MSG_RAID\" or event == \"CHAT_MSG_RAID_LEADER\") and ... then\n        local message = ...\n        \n        if message:match(\"^ACTIVE_RINGS\") then\n            local activeRings = {}\n            \n            for ring in message:gmatch(\"%d\") do\n                table.insert(activeRings, tonumber(ring))\n            end\n            \n            local numRings = #activeRings\n            local num = 1\n            \n            -- Assign units that were on the MRT note\n            for _, unit in ipairs(aura_env.p3Priority) do\n                if aura_env.affected[unit] then\n                    if num <= numRings then -- Primary assignment\n                        allstates[num] = {\n                            show = true,\n                            changed = true,\n                            index = activeRings[num],\n                            unit = unit\n                        }\n                        \n                        if UnitIsUnit(\"player\", unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num], false)\n                        end\n                    elseif num <= 2 * numRings and allstates[num - numRings] then -- Backup assignment\n                        allstates[num - numRings].backup = unit\n                        \n                        if UnitIsUnit(\"player\", unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num - numRings], true)\n                        end\n                    else -- No assignment\n                        allstates[6 + num - 2 * numRings] = {\n                            show = true,\n                            changed = true,\n                            index = 6 + num - 2 * numRings,\n                            unit = unit\n                        }\n                        \n                        if UnitIsUnit(\"player\", unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", 6 + num - 2 * numRings, false)\n                        end\n                    end\n                    \n                    num = num + 1\n                    aura_env.affected[unit] = nil\n                end\n            end\n            \n            -- Assign units that were not on the MRT note\n            for unit in pairs(aura_env.affected) do\n                if num <= numRings then -- Primary assignment\n                    allstates[num] = {\n                        show = true,\n                        changed = true,\n                        index = activeRings[num],\n                        unit = unit\n                    }\n                    \n                    if UnitIsUnit(\"player\", unit) then\n                        WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num], false)\n                    end\n                elseif num <= 2 * numRings and allstates[num - numRings] then -- Backup assignment\n                    allstates[num - numRings].backup = unit\n                    \n                    if UnitIsUnit(\"player\", unit) then\n                        WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num - numRings], true)\n                    end\n                else -- No assignment\n                    allstates[6 + num - 2 * numRings] = {\n                        show = true,\n                        changed = true,\n                        index = 6 + num - 2 * numRings,\n                        unit = unit\n   