gnoreKeyword = {}\nlocal ignoreKeyword = aura_env.config.ignorekeyword\nif type(ignoreKeyword) == \"string\" and ignoreKeyword ~= \"\" then\n    for k in ignoreKeyword:gmatch(\"([^,]+)\") do\n        aura_env.ignoreKeyword[k] = true\n    end\nend\n\naura_env.format = function(line)\n    local formated = line:gsub(\"||c\", \"|c\"):\n    gsub(\"||r\", \"|r\"):\n    gsub(\"{spell:(%d+)}\", GSUB_Icon):\n    gsub(\"{time:([0-9:]+)}\", \"%1\"):\n    gsub(\"{time:([0-9:]+),p(%d+)}\", \"P%2 %1\"):\n    gsub(\"{!?p:[^}]+}(.+){/p}\", \"%1\"):\n    gsub(\"{!?g%d+}(.+){/g}\", \"%1\"):\n    gsub(\"{!?c:[^}]+}(.+){/c}\", \"%1\"):\n    gsub(\"{D}(.+){/D}\", \"%1\"):\n    gsub(\"{H}(.+){/H}\", \"%1\"):\n    gsub(\"{T}(.+){/T}\", \"%1\")\n    for mark, num in pairs(markers) do\n        formated = formated:gsub(\"{\"..mark..\"}\", ICON_LIST[num]..\"0|t\")\n    end\n    \n    return formated \nend\n\nif aura_env.config.bw then\n    WeakAuras.RegisterBigWigsTimer()\nend\nif aura_env.config.dbm then\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerStart\")\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerStop\")\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerUpdate\")\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerPause\")\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerResume\")\n    WeakAuras.RegisterDBMCallback(\"wipe\")\n    WeakAuras.RegisterDBMCallback(\"kill\")\nend\n\nlocal warn = aura_env.config.duration or 15\naura_env.newBar = function(bar, mode)\n    if bar and not bar.cast then\n        local counter\n        local spellId = bar.spellId\n        if spellId then -- update internal counter\n            aura_env.counter[spellId] = aura_env.counter[spellId] and aura_env.counter[spellId] + 1 or 1\n            counter = aura_env.counter[spellId]\n        else\n            counter = 1\n        end\n        if not aura_env.config.internalCounterOnly and bar.count and tonumber(bar.count) ~= 0 then -- use bossmod counter when possible\n            counter = tonumber(bar.count)\n        end\n        local newtimer_created = false\n        for _, check in ipairs(aura_env.checks) do\n            if bar.spellId == check.spellId\n            and (counter == check.count or check.count == 0)\n            then\n                local start = (bar.expirationTime - GetTime()) - warn\n                local encounterStarted = aura_env.encounterStarted\n                local aura_env = aura_env                \n                aura_env.timers[bar] = C_Timer.NewTimer(\n                    start < 0 and 0.1 or start,\n                    function()\n                        WeakAuras.ScanEvents(\n                            \"ERTPEWPEW\",\n                            start,\n                            check.line,\n                            mode,\n                            bar,\n                            encounterStarted,\n                            nil,\n                            aura_env.id\n                        )\n                end)\n                newtimer_created = true\n            end\n        end\n        if aura_env.config.bossmoddebug then\n            print(\n                (\"[DEBUG %s] New %s Bar - text: \\\"%s\\\" - spellId: %s - counter: %s - timer created: %s\"):format(\n                    aura_env.id, mode:upper(), bar.text, tostring(bar.spellId), tostring(counter), tostring(newtimer_created)\n                )\n            )\n        end\n    end\nend\n\nif type(BigWigsLoader) == \"table\" and BigWigsLoader.RegisterMessage then\n    BigWigsLoader.RegisterMessage(\n        {},\n        \"BigWigs_SetStage\",\n        function(event, addon, stage)\n            if stage then\n                WeakAuras.ScanEvents(\"ERTNOTE_PHASE\", stage)\n            end\n        end\n    )\nelseif type(DBM) == \"table\" and DBM.RegisterCallback then\n    DBM:RegisterCallback(\n        \"DBM_SetStage\",\n        function(event, addon, modId, stage, encounterId)\n            if stage then\n                WeakAuras.ScanEvents(\"ERTNOTE_PHASE\", stage)\n     