				["init"] = {
					["custom"] = "-- Honorax@Illidan & Natty :)\n\naura_env.IsEmbalmOiled = function(...)\n    for i = 1, select(\"#\", ...) do\n        local region = select(i, ...)\n        if region and region:GetObjectType() == \"FontString\" then\n            local text = region:GetText()\n            if text and (string.match(text, \"Масло\")) then\n                local _,_, mins = string.find(text, \"%(([1-4]) мин\")\n                local _,_, seconds = string.find(text, \"%((%d+) сек\")\n                if mins then\n                    aura_env.remainingTime = mins * 60\n                    return false\n                elseif seconds then\n                    aura_env.remainingTime = seconds\n                    return false\n                end\n                return true\n            end\n            \n        end\n    end\n    aura_env.remainingTime = 0\n    return false\nend\n\naura_env.remainingTime = 0\naura_env.checkTime = 0\nif not (aura_env.weaponEmbalmFrame) then\n    aura_env.weaponEmbalmFrame = CreateFrame(\"GameTooltip\", \"WeaponEmbalmTooltip\", nil, \"GameTooltipTemplate\")\nend\naura_env.weaponEmbalmFrame:SetOwner(WorldFrame, \"ANCHOR_NONE\")\naura_env.weaponEmbalmFrame:ClearLines()\naura_env.weaponEmbalmFrame:SetInventoryItem(\"player\", 16)\naura_env.weaponEmbalmFrame:AddFontStrings(\n    aura_env.weaponEmbalmFrame:CreateFontString(\"$parentTextLeft1\", nil, \"GameTooltipText\"),\n    aura_env.weaponEmbalmFrame:CreateFontString(\"$parentTextRight1\", nil, \"GameTooltipText\"))\n\nif not aura_env.clickableFrame then\n    local r = WeakAuras.regions['EmbalmersOil'].region\n    aura_env.clickableFrame = CreateFrame(\"Button\",\"EmbalmersOilButton\", r, \"SecureActionButtonTemplate\")  \nend\n\naura_env.clickableFrame:SetAllPoints()\naura_env.clickableFrame:SetAttribute(\"type\", \"macro\")\naura_env.clickableFrame:SetAttribute(\"macrotext\", \"/use Масло бальзамировщика\\n/use 16\\n/click StaticPopup1Button1\")",
