						["custom"] = "function(a, event, ...)\n    local warn = aura_env.config.duration or 15\n    -- init\n    if event == \"ENCOUNTER_START\"\n    and IsAddOnLoaded(\"ExRT\")\n    and (aura_env.config.enableWNoteDisabled or _G.VExRT.Note.enabled)\n    and ... then\n        aura_env.checks = {}\n        aura_env.counter = {}\n        aura_env.timers = {}\n        aura_env.encounterStarted = GetTime()\n        if _G.VExRT.Note.Text1\n        and not aura_env.config.disableShared\n        then\n            local text = _G.VExRT.Note.Text1\n            for line in text:gmatch('[^\\r\\n]+') do\n                local keywordFound = false\n                for k in pairs(aura_env.keywords) do\n                    if line:lower():match(k:lower()) then\n                        keywordFound = true\n                        break\n                    end\n                end\n                if not aura_env.config.filterSharedNote\n                or line:match(WeakAuras.me)\n                or keywordFound\n                then\n                    local mn, sec = line:match(\"{time:(%d+):(%d+)}\")\n                    if mn and sec then\n                        mn = tonumber(mn)\n                        sec = tonumber(sec)\n                        local start = sec + (mn * 60) - warn\n                        local encounterStarted = aura_env.encounterStarted\n                        local aura_env = aura_env\n                        aura_env.timers[start] = C_Timer.NewTimer(\n                            start < 0 and 0.1 or start,\n                            function()\n                                WeakAuras.ScanEvents(\"ERTPEWPEW\", start, aura_env.format(line), \"timed\", start, encounterStarted)\n                            end\n                        )\n                    else\n                        local count, spellId = line:match(\"^[<(](%d+)[>)].-{spell:(%d+)}\")\n                        if count and spellId then\n                            tinsert(aura_env.checks, { spellId = spellId, count = tonumber(count), line = aura_env.format(line) })\n                        end\n                    end\n                end\n            end\n        end\n        if _G.VExRT.Note.SelfText then            \n            local text = _G.VExRT.Note.SelfText\n            for line in text:gmatch('[^\\r\\n]+') do\n                if not aura_env.config.filterPersonalNote\n                or line:match(WeakAuras.me)\n                then\n                    local mn, sec = line:match(\"{time:(%d+):?(%d*)}\")\n                    if mn or sec then\n                        if sec == nil then\n                            sec = tonumber(mn)\n                            mn = 0\n                        else\n                            sec = tonumber(sec)\n                            mn = tonumber(mn)    \n                        end\n                    end\n                    if mn and sec then\n                        local start = sec + (mn * 60) - warn\n                        local encounterStarted = aura_env.encounterStarted\n                        local aura_env = aura_env\n                        C_Timer.After(\n                            start < 0 and 0.1 or start,\n                            function()\n                                WeakAuras.ScanEvents(\"ERTPEWPEW\", start, aura_env.format(line), \"timed\", start, encounterStarted)\n                            end\n                        )\n                    else\n                        local count, spellId = line:match(\"^[<(](%d+)[>)].-{spell:(%d+)}\")\n                        if count and spellId then\n                            tinsert(aura_env.checks, { spellId = spellId, count = tonumber(count), line = aura_env.format(line) })\n                        end\n                    end\n                end\n            end\n        end\n        \n        -- init bars already shown\n        if aura_env.config.bw then\n            for id, bar in pairs(WeakAuras.GetAllBigW