      s.show = false\n                    s.changed = true\n                end\n                if aura_env.timers[bar] then\n                    aura_env.timers[bar]:Cancel()\n                    aura_env.timers[bar] = nil\n                end\n            end\n        end\n    elseif event == \"DBM_TimerStopAll\" and aura_env.config.dbm then\n        for k, v in pairs(a) do\n            if v.type == \"dbm\" then\n                v.show = false\n                v.changed = true\n            end\n        end\n    elseif event == \"BigWigs_StopBars\" and aura_env.config.bw then\n        for k, v in pairs(a) do\n            if v.type == \"bw\" then\n                v.show = false\n                v.changed = true\n            end\n        end\n    elseif event == \"ENCOUNTER_END\" then\n        aura_env.checks = {}\n        aura_env.counter = {}\n        for k, v in pairs(a) do\n            v.show = false\n            v.changed = true\n        end\n        if aura_env.timers then\n            for k, v in pairs(aura_env.timers) do\n                v:Cancel() \n                aura_env.timers[k] = nil\n            end\n        end\n    end\n    return true\nend",
