				["init"] = {
					["custom"] = "-- Don't touch anything in here !\n\n\n\n\nfunction GSUB_Icon(spellID)\n    spellID = tonumber(spellID)\n    \n    local spellTexture = select(3, GetSpellInfo(spellID))\n    spellTexture = \"|T\" .. (spellTexture or \"Interface\\\\Icons\\\\INV_MISC_QUESTIONMARK\") .. \":16|t\"\n    \n    return spellTexture\nend\n\nlocal locals = {\n    --- Raid Target Icon [ENG]\n    {\n        \"star\",\n        \"circle\",\n        \"diamond\",\n        \"triangle\",\n        \"moon\",\n        \"square\",\n        \"cross\",\n        \"skull\",\n    },\n    --- Raid Target Icon [DE]\n    {\n        \"stern\",\n        \"kreis\",\n        \"diamant\",\n        \"dreieck\",\n        \"mond\",\n        \"quadrat\",\n        \"kreuz\",\n        \"totenschädel\",\n    },\n    --- Raid Target Icon [FR]\n    {\n        \"étoile\",\n        \"cercle\",\n        \"losange\",\n        \"triangle\",\n        \"lune\",\n        \"carré\",\n        \"croix\",\n        \"crâne\",\n    },\n    --- Raid Target Icon [IT]\n    {\n        \"stella\",\n        \"cerchio\",\n        \"rombo\",\n        \"triangolo\",\n        \"luna\",\n        \"quadrato\",\n        \"croce\",\n        \"teschio\",\n    },\n    --- Raid Target Icon [RU]\n    {\n        \"звезда\",\n        \"круг\",\n        \"ромб\",\n        \"треугольник\",\n        \"полумесяц\",\n        \"квадрат\",\n        \"крест\",\n        \"череп\",\n    }\n}\n\nlocal markers = {}\nfor _, loc in ipairs(locals) do\n    for j, v in ipairs(loc) do\n        markers[v] = j\n    end\nend\nfor k, v in pairs(ICON_TAG_LIST) do\n    markers[k] = v\nend\n\naura_env.keywords = {}\nlocal keywords = aura_env.config.customkeyword\nif type(keywords) == \"string\" and keywords ~= \"\" then\n    for k in keywords:gmatch(\"([^,]+)\") do\n        aura_env.keywords[k] = true\n    end\nend\n\naura_env.format = function(line)\n    local formated = line:gsub(\"||c\", \"|c\"):\n    gsub(\"||r\", \"|r\"):\n    gsub(\"{spell:(%d+)}\", GSUB_Icon):\n    gsub(\"{time:([0-9:]+)}\", \"%1\")\n    for mark, num in pairs(markers) do\n        formated = formated:gsub(\"{\"..mark..\"}\", ICON_LIST[num]..\"0|t\")\n    end\n    return formated \nend\n\nif aura_env.config.bw then\n    WeakAuras.RegisterBigWigsTimer()\nend\nif aura_env.config.dbm then\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerStart\")\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerStop\")\n    WeakAuras.RegisterDBMCallback(\"DBM_TimerUpdate\")\n    WeakAuras.RegisterDBMCallback(\"wipe\")\n    WeakAuras.RegisterDBMCallback(\"kill\")\nend\n\nlocal warn = aura_env.config.duration or 15\naura_env.newBar = function(bar, mode)\n    if bar and not bar.cast then\n        local counter\n        local spellId = bar.spellId\n        if spellId then\n            if aura_env.counter[spellId] then\n                aura_env.counter[spellId] = aura_env.counter[spellId] + 1\n            else\n                aura_env.counter[spellId] = 1\n            end\n            counter = aura_env.counter[spellId]\n        else\n            counter = 0\n        end\n        for _, check in ipairs(aura_env.checks) do\n            if bar.spellId == check.spellId\n            and (counter == check.count or check.count == 0)\n            then\n                local start = (bar.expirationTime - GetTime()) - warn\n                local encounterStarted = aura_env.encounterStarted\n                aura_env.timers[bar] = C_Timer.NewTimer(\n                    start < 0 and 0.1 or start,\n                    function()\n                        WeakAuras.ScanEvents(\n                            \"ERTPEWPEW\",\n                            start,\n                            check.line,\n                            mode,\n                            bar,\n                            encounterStarted\n                        )\n                    end\n                )\n            end\n        end\n    end\nend",
