						["custom"] = "function(allstates, event)\n  -- set up thje base state\n  if not allstates[\"\"] then \n    allstates[\"\"] = {\n      show = true,\n      changed = true,\n      progressType = \"static\",\n      value = UnitHealth(\"player\"),\n      total = UnitHealthMax(\"player\"),\n      absorb = UnitGetTotalAbsorbs(\"player\") or 0\n    }\n  end\n  local s = allstates[\"\"]\n  s.changed = true\n  -- handle events\n  if event == \"UNIT_HEALTH\" then\n    s.value = UnitHealth(\"player\")\n  elseif event == \"UNIT_MAXHEALTH\" then\n    s.total = UnitHealthMax(\"player\")\n  elseif event == \"UNIT_ABSORB_AMOUNT_CHANGED\" then\n    s.absorb = UnitGetTotalAbsorbs(\"player\") or 0\n  end\n  \n  --update state with new values\n  s.percent = s.value/s.total*100\n  s.missing = s.total - s.value\n  \n  -- setup overlay\n  s.additionalProgress = s.additionalProgress or {}\n  s.additionalProgress[1] = {}\n  local overlay = s.additionalProgress[1]\n  \n  --calculate overlay\n  if s.absorb < s.missing then\n    overlay.direction = \"forward\"\n    overlay.width = s.absorb\n  else\n    overlay.min = math.max(s.total-s.absorb, 0)\n    overlay.max = s.total\n  end\n  \n  return true\nend",
