						["debuffType"] = "HELPFUL",
						["spellIds"] = {
						},
						["custom"] = "function(event, ...)\n    if (aura_env.config.settingsGroup.requireLead and not UnitIsGroupLeader(\"player\")) then return end \n    -- doesn't do marking if not player lead and \"not lead\" is toggled in custom options\n    --handles premarked mobs\n    \n    ---------------------------------------------------------------------------------------\n    if event == \"PLAYER_REGEN_DISABLED\" or event == \"PLAYER_REGEN_ENABLED\" then\n        aura_env.marks = {}\n        aura_env.assigned = {} \n        for i = 1,40 do\n            local premarked = \"nameplate\"..i\n            if UnitExists(premarked) and GetRaidTargetIndex(premarked) and not UnitPlayerControlled(premarked) then\n                if not aura_env.mobList[UnitGUID(premarked)] then\n                    aura_env.assigned[GetRaidTargetIndex(premarked)] = true\n                    aura_env.marks[UnitGUID(premarked)] = GetRaidTargetIndex(premarked)\n                end\n            end\n        end        \n        aura_env.setTankMark()\n        --handles premarked players\n        for unit in WA_IterateGroupMembers() do\n            if GetRaidTargetIndex(unit) then\n                aura_env.assigned[GetRaidTargetIndex(unit)] = true\n                aura_env.marks[unit] = GetRaidTargetIndex(unit)\n            end\n        end\n    end\n    \n    \n    \n    ---------------------------------------------------------------------------------------\n    --mouseover functionality\n    if event == \"UPDATE_MOUSEOVER_UNIT\" and aura_env.config.settingsGroup.mouseover then\n        if (aura_env.config.settingsGroup.ShiftDown and not IsShiftKeyDown()) then return end\n        local guid = UnitGUID(\"mouseover\")\n        if not guid then return end\n        local unitid = select(6,strsplit(\"-\",guid))\n        if UnitExists(\"mouseover\") and \n        not GetRaidTargetIndex(\"mouseover\") and \n        not aura_env.marks[\"mouseover\"] and \n        aura_env.mobList[unitid] and \n        not UnitIsDead(\"mouseover\") and \n        not aura_env.marks[UnitGUID(\"mouseover\")]then\n            --handles premarked players\n            for unit in WA_IterateGroupMembers() do\n                if GetRaidTargetIndex(unit) then\n                    aura_env.assigned[GetRaidTargetIndex(unit)] = true\n                    aura_env.marks[unit] = GetRaidTargetIndex(unit)\n                end            \n            end\n            local order = aura_env.getOrder(unitid)\n            \n            for i=1, #order do\n                if not aura_env.assigned[order[i]] and aura_env.config.settingsGroup.enabledMarks[order[i]] then\n                    aura_env.marks[guid] = order[i]\n                    aura_env.assigned[order[i]] = true\n                    SetRaidTarget(\"mouseover\", order[i])\n                    return\n                end\n            end\n        end\n    end\n    \n    \n    \n    ---------------------------------------------------------------------------------------\n    --combat marking\n    if event == \"UNIT_COMBAT\" and aura_env.config.settingsGroup.regularMarking then\n        local unit = select(1, ...)\n        local guid = UnitGUID(unit)\n        if unit == \"player\" then return end\n        \n        if not guid then return end        \n        local unitid = select(6, strsplit(\"-\", guid))\n        if UnitExists(unit) and UnitAffectingCombat(unit) and UnitCanAttack(\"player\", unit) and not GetRaidTargetIndex(unit) and not aura_env.marks[guid] and aura_env.mobList[unitid] then\n            \n            local order = aura_env.getOrder(unitid)\n            for i=1, #order do\n                if not aura_env.assigned[order[i]] and aura_env.config.settingsGroup.enabledMarks[order[i]] then\n                    aura_env.marks[guid] = order[i]\n                    aura_env.assigned[order[i]] = true\n                    SetRaidTarget(unit, order[i])\n                    return\n                end                \n            end    \n        end\n        \n    end\n    \n    \n    \n    \n    ---------------------------------------------------------------------------------------\n    --Frees up marks when mobs die\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and (select(2,...) == \"UNIT_DIED\" or select(2,...) == \"UNIT_DESTROYED\") then\n        local guid = select(8,...)        \n        if guid and(aura_env.marks[guid]) then\n            local type = select(1, strsplit(\"-\", guid)) \n            \n            if string.lower(type)~=\"player\" then\n                aura_env.assigned[aura_env.marks[select(8,...)]] = false\n                aura_env.marks[select(8,...)] = nil\n            end\n        end    \n    end\n    \nend",
