						["custom"] = "function(allstates, event, ...)\n    if event == \"ENCOUNTER_START\" then\n        aura_env.GUID2Unit = {}\n        aura_env.affected = {}\n        aura_env.intermissionPriority = {}\n        aura_env.p3Priority = {}\n        aura_env.phase = 1\n        aura_env.count = 0\n        \n        local p3Specific = false -- Specific assignments for p3\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUID2Unit[UnitGUID(unit)] = unit\n        end\n        \n        if (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\")) and _G.VExRT.Note.Text1 then\n            local text = _G.VExRT.Note.Text1\n            \n            -- Set intermission priority\n            local start = false\n            local priority = 1\n            \n            for line in text:gmatch(\"[^\\r\\n]+\") do\n                if start then\n                    if line:match(\"^intermissionEnd\") then break end\n                    \n                    local unit\n                    \n                    for u in WA_IterateGroupMembers() do\n                        local name = UnitName(u)\n                        name = Ambiguate(name, \"none\")\n                        \n                        if line:match(name) then\n                            unit = u\n                            break\n                        end\n                    end\n                    \n                    if unit then\n                        aura_env.intermissionPriority[priority] = unit\n                        priority = priority + 1\n                    end\n                end\n                \n                if line:match(\"^intermissionStart\") then start = true end\n            end\n            \n            -- Set p3 priority\n            start = false\n            priority = 1\n            \n            for line in text:gmatch(\"[^\\r\\n]+\") do\n                if start then\n                    if line:match(\"^p3End\") then break end\n                    \n                    local unit\n                    \n                    for u in WA_IterateGroupMembers() do\n                        local name = UnitName(u)\n                        name = Ambiguate(name, \"none\")\n                        \n                        if line:match(name) then\n                            unit = u\n                            break\n                        end\n                    end\n                    \n                    if unit then\n                        aura_env.p3Priority[priority] = unit\n                        priority = priority + 1\n                    end\n                end\n                \n                if line:match(\"^p3Start\") then\n                    start = true\n                    p3Specific = true\n                end\n            end\n        else\n            message(\"Please turn on your MRT AddOn and enable the note\")\n        end\n        \n        -- If there is no priority list for p3, use the intermission list\n        if not p3Specific then\n            aura_env.p3Priority = aura_env.intermissionPriority\n        end\n    elseif event == \"BDGG_RUNIC_AFFINITY\" and ... then\n        local priorityList = aura_env.phase == 3 and aura_env.p3Priority or aura_env.intermissionPriority\n        local num = 1\n        \n        -- Assign units that were on the MRT note\n        for _, unit in ipairs(priorityList) do\n            if aura_env.affected[unit] then\n                allstates[num] = {\n                    show = true,\n                    changed = true,\n                    index = num,\n                    unit = unit\n                }\n                \n                if UnitIsUnit(\"player\", unit) then\n                    WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, false)\n                end\n                \n                num = num + 1\n                aura_env.affected[unit] = nil\n            end\n        end\n        \n        -- Assign units that were not on the MRT note\n        for unit in pairs(aura_env.affected) do\n            allstates[num] = {\n                show = true,\n                changed = true,\n                index = num,\n                unit = unit\n            }\n            \n            if UnitIsUnit(\"player\", unit) then\n                WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, false)\n            end\n            \n            num = num + 1\n        end\n        \n        return true\n    elseif (event == \"CHAT_MSG_RAID\" or event == \"CHAT_MSG_RAID_LEADER\") and ... then\n        local message = ...\n        \n        if message:match(\"^ACTIVE_RINGS\") then\n            local activeRings = {}\n            \n            for ring in message:gmatch(\"%d\") do\n                table.insert(activeRings, tonumber(ring))\n            end\n            \n            local numRings = #activeRings\n            local num = 1\n            \n            -- Assign units that were on the MRT note\n            for _, unit in ipairs(aura_env.p3Priority) do\n                if aura_env.affected[unit] then\n                    if num <= numRings then -- Primary assignment\n                        allstates[num] = {\n                            show = true,\n                            changed = true,\n                            index = activeRings[num],\n                            unit = unit\n                        }\n                        \n                        if UnitIsUnit(\"player\", unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num], false)\n                        end\n                    elseif num <= 2 * numRings and allstates[num - numRings] then -- Backup assignment\n                        allstates[num - numRings].backup = unit\n                        \n                        if UnitIsUnit(\"player\", unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num - numRings], true)\n                        end\n                    else -- No assignment\n                        allstates[6 + num - 2 * numRings] = {\n                            show = true,\n                            changed = true,\n                            index = 6 + num - 2 * numRings,\n                            unit = unit\n                        }\n                        \n                        if UnitIsUnit(\"player\", unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", 6 + num - 2 * numRings, false)\n                        end\n                    end\n                    \n                    num = num + 1\n                    aura_env.affected[unit] = nil\n                end\n            end\n            \n            -- Assign units that were not on the MRT note\n            for unit in pairs(aura_env.affected) do\n                if num <= numRings then -- Primary assignment\n                    allstates[num] = {\n                        show = true,\n                        changed = true,\n                        index = activeRings[num],\n                        unit = unit\n                    }\n                    \n                    if UnitIsUnit(\"player\", unit) then\n                        WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num], false)\n                    end\n                elseif num <= 2 * numRings and allstates[num - numRings] then -- Backup assignment\n                    allstates[num - numRings].backup = unit\n                    \n                    if UnitIsUnit(\"player\", unit) then\n                        WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", activeRings[num - numRings], true)\n                    end\n                else -- No assignment\n                    allstates[6 + num - 2 * numRings] = {\n                        show = true,\n                        changed = true,\n                        index = 6 + num - 2 * numRings,\n                        unit = unit\n                    }\n                    \n                    if UnitIsUnit(\"player\", unit) then\n                        WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", 6 + num - 2 * numRings, false)\n                    end\n                end\n                \n                num = num + 1\n            end\n            \n            return true\n        end\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellId = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" and spellId == 354964 then -- Runic Affinity\n            local unit = aura_env.GUID2Unit[destGUID]\n            aura_env.affected[unit] = true\n            aura_env.count = aura_env.count + 1\n            \n            if aura_env.phase < 3 and aura_env.count == 1 then\n                C_Timer.After(0.3, function() WeakAuras.ScanEvents(\"BDGG_RUNIC_AFFINITY\", true) end)\n            end\n        elseif subEvent == \"SPELL_AURA_REMOVED\" then\n            if spellId == 357739 then\n                aura_env.phase = aura_env.phase + 1\n            end\n            \n            if spellId == 357739 or spellId == 353195 then -- Realign Fate / Extemporaneous Fate\n                aura_env.count = 0\n                aura_env.affected = {}\n                \n                for _, state in pairs(allstates) do\n                    state.show = false\n                    state.changed = true\n                end\n                \n                return true\n            end\n        elseif subEvent == \"UNIT_DIED\" and aura_env.GUID2Unit[destGUID] then\n            local unit = aura_env.GUID2Unit[destGUID]\n            local num\n            \n            -- Check if the unit that died is assigned to a rune\n            for k, state in pairs(allstates) do\n                if state.unit == unit then\n                    num = k\n                    allstates[k].dead = true\n                end\n            end\n            \n            -- If the unit already had a backup, don't do anything\n            if num and allstates[num].backup then return true end\n            \n            -- If a backup dies just remove them\n            if num and num > 6 then\n                allstates[num].show = false\n                allstates[num].changed = true\n                return true\n            end\n            \n            -- If they are not assigned to a rune, check if they are assigned backup to a rune\n            if not num then\n                for k, state in pairs(allstates) do\n                    if state.backup == unit then\n                        state.backup = nil\n                        state.changed = true\n                        num = k\n                    end\n                end\n            end\n            \n            if num then\n                -- If there is a tank available for backups, assign them\n                for i = 7, 11 do\n                    local s = allstates[i]\n                    \n                    if s and UnitGroupRolesAssigned(s.unit) == \"DAMAGER\" then\n                        allstates[num].backup = s.unit\n                        s.show = false\n                        s.changed = true\n                        \n                        if UnitIsUnit(\"player\", s.unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, true)\n                        end\n                        \n                        return true\n                    end\n                    \n                    if s and UnitGroupRolesAssigned(s.unit) == \"HEALER\" then\n                        allstates[num].backup = s.unit\n                        s.show = false\n                        s.changed = true\n                        \n                        if UnitIsUnit(\"player\", s.unit) then\n                            WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, true)\n                        end\n                        \n                        return true\n                    end\n                end\n                \n                -- Otherwise, assign a backup based on class\n                for _, class in ipairs(aura_env.backupClasses) do\n                    for i = 7, 11 do\n                        local s = allstates[i]\n                        \n                        if s then\n                            if class == UnitClassBase(s.unit) then\n                                allstates[num].backup = s.unit\n                                s.show = false\n                                s.changed = true\n                                \n                                if UnitIsUnit(\"player\", s.unit) then\n                                    WeakAuras.ScanEvents(\"BDGG_RUNE_ASSIGNED\", num, true)\n                                end\n                                \n                                return true\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    end\nend",
