				["init"] = {
					["custom"] = "-- Slightly faster for the loop\nlocal GetContainerItemInfo = GetContainerItemInfo\nlocal GetContainerNumSlots = GetContainerNumSlots\nlocal NUM_BAG_SLOTS = NUM_BAG_SLOTS\nlocal select = select\n\n-- Custom option keys\naura_env.options = {'party', 'guild', 'spam'}\n\n-- Custom option table\naura_env.types = {}\n\n-- Build custom options\nfor _, v in ipairs(aura_env.options) do\n    if aura_env.config['enabled'..v] then\n        aura_env.types[v] = true\n    end\nend\n\n-- Keystone item IDs\naura_env.ids = {\n    [138019] = true, -- Legion\n    [158923] = true, -- BfA\n    [180653] = true, -- Shadowlands\n    [151086] = true, -- Tournament\n}\n\n-- Force update on init\naura_env.key = nil\n\n-- Search the bags for the item IDs from the table above\naura_env.update = function()\n    for bag = 0, NUM_BAG_SLOTS do\n        local bagSlots = GetContainerNumSlots(bag)\n        for slot = 1, bagSlots do\n            local itemLink, _, _, itemID = select(7, GetContainerItemInfo(bag, slot))\n            if aura_env.ids[itemID] then\n                aura_env.key = itemLink\n                return\n            end\n        end\n    end\nend\n\n-- Send message to channel\naura_env.link = function(channel)\n    -- No key found, force update\n    if not aura_env.key then aura_env.update() end\n    \n    -- Key found, link it in chat and add current covenant in brackets\n    if aura_env.key then\n        if aura_env.covenantName then\n            SendChatMessage(aura_env.key..' ('..aura_env.covenantName..')', channel)\n        else\n            SendChatMessage(aura_env.key, channel)\n        end\n    end\nend\n\n-- Add covenant data\naura_env.covenantID = C_Covenants.GetActiveCovenantID()\naura_env.covenantData = aura_env.covenantID and C_Covenants.GetCovenantData(aura_env.covenantID)\nif aura_env.covenantData then\n    aura_env.covenantName = aura_env.covenantData.name\nend\n\ndo -- Anti-Spam check\n    local spamTimes = { GUILD = 0, PARTY = 0 }\n    aura_env.isSpam = function(channel)\n        local current = GetTime()\n        if current > spamTimes[channel] then\n            spamTimes[channel] = current + 30 -- next allowed time\n            return false\n        else\n            return true -- its spam still\n        end\n    end\nend",
