			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "local a = aura_env\n\na.spells = {\n    [190984] = 6,  -- Wrath\n    [194153] = 8, --  StarFire\n    [274281] = 10, -- New Moon\n    [274282] = 20, -- Half Moon\n    [274283] = 40, -- Full Moon\n    [202347] = 8, -- Stellar Flare\n}\n\n\na.getPredicted = function(spellId, s)\n    local castEnd = select(5, UnitCastingInfo(\"player\")) / 1000\n    \n    return \n    -- lucid dream *2 mult\n    (s.lucidEnd and castEnd and s.lucidEnd >= castEnd and 2 or 1)\n    \n    -- solar eclipse and sotf talent 1.5 mult on wrath\n    * (s.sotf and spellId == 190984 and s.solarEnd and castEnd and s.solarEnd >= castEnd and 1.5 or 1)\n    * (aura_env.spells[spellId] or 0)\n    \nend\n\n\na.getFormattedAsPText = function(s)\n    if aura_env.config.aspMode == 1 then -- predicted\n        if s.predict > s.total then\n            return WrapTextInColorCode(s.predict, \"ffff0000\")\n        else    \n            return s.predict\n        end\n    end\n    \n    if aura_env.config.aspMode == 2 then -- current (predicted))\n        if s.predict > s.total then\n            return s.value .. WrapTextInColorCode(\" (! \" .. s.predict .. \" !)\", \"ffff0000\") \n        elseif s.incoming > 0 then\n            return s.value .. \" (\" .. s.predict .. \")\"\n        else    \n            return s.value\n        end\n        \n    end\n    \n    if aura_env.config.aspMode == 3 then -- current\n        return s.value\n    end\n    \nend\n\n\na.getStarsurgeOverlayValues = function(s)\n    return a.getSpenderOverlayValues(s,30)\nend\n\na.getStarfallOverlayValues = function(s)\n    return a.getSpenderOverlayValues(s,50)\nend\n\n-- TODO: check config for show when low and return empty\na.getSpenderOverlayValues = function(s, cost)\n    local tdReduction = .10\n    local tickPos = cost - s.tdStacks * tdReduction * cost\n    \n    local val = s.predict < tickPos and tickPos\n    or (s.predict > s.total) and s.total - tickPos\n    or (s.predict - tickPos)\n    \n    return {min = val, max = val+1}    \n    \nend\n\n\n\n-- glowing spark (from https://wago.io/UniversalPowerBar)\nlocal r = aura_env.region\nlocal b = r.bar\n\nif not b.modelFrame then\n    local modelFrame = CreateFrame(\"FRAME\", nil, b)\n    b.modelFrame = modelFrame\n    local sparkle = CreateFrame(\"PlayerModel\", nil, b.modelFrame)\n    b.modelFrame.sparkle = sparkle\n    b.modelFrame.sparkle:SetKeepModelOnHide(true)\nend\n\nb.modelFrame.sparkle:SetModel(1630153)\nb.modelFrame.sparkle:ClearTransform()\nb.modelFrame.sparkle:SetPosition(4,0.32,1.85,0)\nlocal h = b:GetHeight()\nb.modelFrame.sparkle:SetPoint(\"RIGHT\", b.modelFrame)\nb.modelFrame.sparkle:SetSize(h*2, h)\nb.modelFrame.sparkle:SetAlpha(0.20)\n\nb.modelFrame:SetAllPoints(b.fg)\nb.modelFrame:SetClipsChildren(true)\nb.modelFrame:Show()",
