						["event"] = "Health",
						["unit"] = "player",
						["subeventPrefix"] = "SPELL",
						["custom"] = "function(s, event, unit, arg1, arg2)\n    \n    if not s[\"\"] then \n        \n        -- we only need to use this method if there are talents that make decimal values (BotA)\n        -- local value = UnitPower(\"player\", Enum.PowerType.LunarPower, true) / 10 or 0\n        local value = UnitPower(\"player\", Enum.PowerType.LunarPower) or 0\n        local total = UnitPowerMax(\"player\", Enum.PowerType.LunarPower) or 1\n        \n        s[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"static\",\n            \n            additionalProgress = {{min = value, max = total},{},{},{}},\n            \n            -- Astral Power\n            value = value,\n            total = total,\n            predict = value,\n            incoming = 0,\n            formattedAsP = value,\n            \n            -- Talents\n            sotf = IsPlayerSpell(114107),\n            \n            -- Buffs\n            moonkin = GetPlayerAuraBySpellID(24858) and true or false,\n            ca = (GetPlayerAuraBySpellID(102560) or GetPlayerAuraBySpellID(194223)) and true or false,\n            solar = GetPlayerAuraBySpellID(48517) and true or false,\n            lunar = GetPlayerAuraBySpellID(48518) and true or false,\n            lucid = GetPlayerAuraBySpellID(298357) and true or false,\n            tdStacks = 0,\n            \n            -- ExpirationTimes\n            solarEnd = nil,\n            lucidEnd = nil,\n            \n            -- Custom Options\n            --glowEclipse = aura_env.config.glows.eclipse,\n            highAsp = false,\n            showTicks = aura_env.config.ticks == 1 or aura_env.config.ticks == 2,\n            showTicksLow = aura_env.config.ticks == 2\n            \n        }\n    end\n    \n    if event == \"UPDATE_SHAPESHIFT_FORM\" then\n        s[\"\"].moonkin = GetPlayerAuraBySpellID(24858) and true or false\n        s[\"\"].changed = true\n        \n        \n    elseif event == \"PLAYER_TALENT_UPDATE\" then\n        s[\"\"].sotf = IsPlayerSpell(114107)\n        \n        \n    elseif event == \"UNIT_AURA\" and unit == \"player\" then\n        \n        s[\"\"].solarEnd = select(6, GetPlayerAuraBySpellID(48517))\n        s[\"\"].solar = s[\"\"].solarEnd and true or false\n        \n        s[\"\"].lucidEnd = select(6, GetPlayerAuraBySpellID(298357))\n        s[\"\"].lucid = s[\"\"].lucidEnd and true or false\n        \n        s[\"\"].lunar = GetPlayerAuraBySpellID(48518) and true or false\n        s[\"\"].ca = (GetPlayerAuraBySpellID(102560) or GetPlayerAuraBySpellID(194223)) and true or false\n        \n        s[\"\"].tdStacks = select(3,GetPlayerAuraBySpellID(340049)) or 0\n        \n        s[\"\"].formattedAsP = aura_env.getFormattedAsPText(s[\"\"])\n        \n        s[\"\"].changed = true\n        \n        \n        \n    elseif event == \"UNIT_DISPLAYPOWER\" or event == \"LOAD\" or event == \"PLAYER_SPECIALIZATION_CHANGED\"\n    or event == \"UNIT_MAXPOWER\" or event == \"UNIT_POWER_FREQUENT\" then\n        \n        if event ~= \"UNIT_POWER_FREQUENT\" then\n            s[\"\"].total = UnitPowerMax(\"player\",Enum.PowerType.LunarPower)\n        end\n        \n        s[\"\"].value = UnitPower(\"player\", Enum.PowerType.LunarPower) or 0\n        \n        s[\"\"].predict = s[\"\"].value + s[\"\"].incoming\n        \n        s[\"\"].formattedAsP = aura_env.getFormattedAsPText(s[\"\"])\n        \n        s[\"\"].additionalProgress[1] = {min = s[\"\"].value, max = s[\"\"].total}\n        \n        s[\"\"].highAsp = aura_env.config.glows.asp > 0 and aura_env.config.glows.asp <= s[\"\"].predict\n        \n        s[\"\"].changed = true\n        \n        \n        \n    elseif event == \"UNIT_SPELLCAST_START\" and aura_env.spells[arg2] then\n        \n        s[\"\"].incoming = aura_env.getPredicted(arg2,s[\"\"])\n        s[\"\"].predict = s[\"\"].value + s[\"\"].incoming\n        \n        s[\"\"].formattedAsP = aura_env.getFormattedAsPText(s[\"\"])\n        \n        s[\"\"].additionalProgress[1] = {min = s[\"\"].value, max = s[\"\"].total}\n        s[\"\"].additionalProgress[2] = {direction = \"forward\", width = s[\"\"].incoming}\n        \n        s[\"\"].highAsp = aura_env.config.glows.asp > 0 and aura_env.config.glows.asp <= s[\"\"].predict\n        \n        s[\"\"].changed = true\n        \n        \n    elseif event == \"UNIT_SPELLCAST_STOP\" then\n        \n        s[\"\"].incoming = 0\n        s[\"\"].predict = s[\"\"].value\n        \n        s[\"\"].formattedAsP = aura_env.getFormattedAsPText(s[\"\"])\n        \n        s[\"\"].additionalProgress =  {\n            {min = s[\"\"].value, max = s[\"\"].total},\n            {},\n            {},\n            {}\n        }\n        \n        s[\"\"].highAsp = aura_env.config.glows.asp > 0 and aura_env.config.glows.asp <= s[\"\"].predict\n        \n        s[\"\"].changed = true\n    end\n    \n    \n    if s[\"\"].showTicks then\n        s[\"\"].additionalProgress[3] = aura_env.getStarsurgeOverlayValues(s[\"\"])\n        s[\"\"].additionalProgress[4] = aura_env.getStarfallOverlayValues(s[\"\"])\n    end\n    \n    \n    return true\nend",
