						["customIcon"] = "",
						["check"] = "event",
						["custom"] = "function(event, unitId, countdown)\n    if event == \"READY_CHECK\" or event == \"CHALLENGE_MODE_START\" or event == \"PARTY_MEMBER_ENABLE\" or event == \"PLAYER_ENTERING_WORLD\" or event == \"GROUP_ROSTER_UPDATE\" then \n        -- Send information about yourself\n        aura_env.sendPlayerInfo()\n        -- Update playermark\n        aura_env.updatePlayerMarks()\n    end\n    if event == \"CHALLENGE_MODE_START\" then \n        -- Add Seasonal Mobs to table\n        WeakAuras.ScanEvents(\"AUTOMARK_GET_MOBS\", C_MythicPlus.GetCurrentSeason())\n    end\n    if event == \"GROUP_ROSTER_UPDATE\" then \n        for player in pairs(aura_env.partyMembers) do \n            local member = false\n            for unit in WA_IterateGroupMembers() do\n                local name = unit == \"player\" and string.format(\"%s-%s\", UnitFullName(unit)) or string.format(\"%s-%s\", UnitName(unit), GetNormalizedRealmName(unit))\n                if player == name then member = true break end \n            end\n            if not member then aura_env.partyMembers[player] = nil end -- Player has left party, remove as member. \n        end\n        -- if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.partyMembers, \"aura_env.partyMembers\") end\n    end\n    if event == \"START_TIMER\" and countdown > 0 and not select(3, C_ChallengeMode.GetActiveKeystoneInfo()) then \n        return true\n    end\n    -- Player is not marker\n    if not aura_env.partyMembers[aura_env.GetPlayerName()].marker then return end\n    -- Automark: Mouseover\n    if (event == \"UPDATE_MOUSEOVER_UNIT\" or event == \"MODIFIER_STATE_CHANGED\") \n    and UnitExists(\"mouseover\") \n    and not UnitIsDead(\"mouseover\") \n    and not UnitIsPlayer(\"mouseover\") \n    and not GetRaidTargetIndex(\"mouseover\") then\n        -- Grabbing some values\n        local guid, npcID = UnitGUID(\"mouseover\"), select(6, strsplit(\"-\", UnitGUID(\"mouseover\")))\n        npcID = tonumber(npcID)\n        if not aura_env.mobs[npcID] then return end -- NPC is not in table\n        local _, maxRange = aura_env.rc:GetRange('mouseover')\n        local key = {\n            [1] = true,\n            [2] = IsShiftKeyDown(),\n            [3] = IsControlKeyDown(),\n            [4] = IsAltKeyDown(),\n        }\n        -- Does the npcID exist in our table?\n        if aura_env.mobs[npcID] \n        and aura_env.mobs[npcID].autoMarkOption == 2 \n        and key[aura_env.config.settings.mouseover.modifier] \n        and (aura_env.mobs[npcID].distance == 10\n            and aura_env.range[aura_env.config.settings.mouseover.markDistance](maxRange, guid)\n            or aura_env.range[aura_env.mobs[npcID].distance](maxRange, guid)) then\n            aura_env.CreateMarkOnUnitId(\"mouseover\", guid)\n        end\n    end\n    -- Automark: Nameplate\n    if event == \"NAME_PLATE_UNIT_ADDED\" and unitId then\n        local guid, npcID = UnitGUID(unitId), select(6, strsplit(\"-\", UnitGUID(unitId)))\n        npcID = tonumber(npcID)\n        if aura_env.mobs[npcID] and aura_env.mobs[npcID].autoMarkOption == 3 then\n            aura_env.CreateMarkOnUnitId(unitId, guid)\n        end\n    end\n    -- Automark: Nameplate + combat \n    if event == \"UNIT_THREAT_LIST_UPDATE\" and unitId and UnitAffectingCombat(unitId) and UnitExists(unitId) and GetRaidTargetIndex(unitId) == nil then\n        local guid, npcID = UnitGUID(unitId), select(6, strsplit(\"-\", UnitGUID(unitId)))\n        npcID = tonumber(npcID)\n        if aura_env.mobs[npcID] and aura_env.mobs[npcID].autoMarkOption == 4 then\n            aura_env.CreateMarkOnUnitId(unitId, guid)\n        end\n    end\nend\n\n-- GROUP_ROSTER_UPDATE, UPDATE_MOUSEOVER_UNIT, NAME_PLATE_UNIT_ADDED, UNIT_THREAT_LIST_UPDATE, READY_CHECK, CHALLENGE_MODE_START, PARTY_MEMBER_ENABLE, START_TIMER, MODIFIER_STATE_CHANGED",
