						["custom"] = "function(event, _, subEvent, _, _, _, _, _, guid)\n    if event == \"INITIALIZE\" then \n        WeakAuras.ScanEvents(\"AUTOMARK_GET_MOBS\", aura_env.GetInstanceID())\n        aura_env.sendRoleCall()\n        -- if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.mobs, \"aura_env.mobs\") end\n    end\n    if event == \"PLAYER_REGEN_ENABLED\" then \n        -- Player is out of combat, reset marks.\n        for k in pairs(aura_env.marks) do \n            aura_env.marks[k].active = false\n            aura_env.marks[k].next = {}\n        end\n        -- Player is out of combat, reset steps.\n        aura_env.step = {}\n        -- Check if tank/healer is still marked properly\n        aura_env.updatePlayerMarks()\n    end\n    if event == \"PLAYER_ENTERING_WORLD\" then \n        -- Player has relogged/entered dungeon, update the instanceID\n        aura_env.sendPlayerInfo()\n        aura_env.updatePlayerMarks()\n    end\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and subEvent == \"UNIT_DIED\" and aura_env.config[aura_env.GetInstanceID()] then\n        local npcID = select(6, strsplit(\"-\", guid))\n        if aura_env.mobs[tonumber(npcID)] then \n            local mark\n            for k,v in pairs(aura_env.marks) do \n                if v.guid == guid then \n                    aura_env.marks[k].active = false\n                    mark = k\n                    break\n                end\n            end\n            if mark and aura_env.marks[mark].next and aura_env.mobs[tonumber(npcID)].build == 2 then \n                local g, nameplate = next(aura_env.marks[mark].next)\n                if nameplate and g then \n                    aura_env.CreateMarkOnUnitId(nameplate.nameplate, g, mark) -- Mark next in line\n                    aura_env.marks[mark].next[g] = nil -- Remove npc from queue\n                end\n            end\n        end\n    end\nend\n\n-- INITIALIZE, PLAYER_ENTERING_WORLD, CLEU:UNIT_DIED, PLAYER_REGEN_ENAB