						["custom"] = "function(event, prefix, text, channel, sender)\n    if event == \"CHAT_MSG_ADDON\" and prefix == \"MYTHICAUTOMARK\" then \n        local subEvent, msg = strsplit(\"^\", text)\n        if subEvent == \"UPDATE_ME\" then \n            aura_env.sendPlayerInfo()\n        end\n        if subEvent == \"WHO_MARK\" then \n            local senderRole, senderMarkMobs, senderMarkPlayers, senderLeader, senderDeciderValue, senderBuildOption = aura_env.convertAddonMsg(msg)\n            local marker, markerRole, markerMobs, markerLeader, markerDeciderValue, markerBuildOption\n            local change = false\n            if not senderBuildOption then senderBuildOption = 1 end -- Legacy support. \n            if not aura_env.partyMembers[sender] then -- New player\n                aura_env.partyMembers[sender] = {\n                    [\"role\"] = senderRole,\n                    [\"mobs\"] = senderMarkMobs,\n                    [\"players\"] = senderMarkPlayers,\n                    [\"leader\"] = senderLeader,\n                    [\"decider\"] = senderDeciderValue,\n                    [\"build\"] = senderBuildOption,\n                }\n            end\n            for k,v in pairs(aura_env.partyMembers) do \n                if v.marker then \n                    marker, markerRole, markerMobs, markerLeader, markerDeciderValue, markerBuildOption = k, v.role, v.mobs, v.leader, v.decider, v.build\n                end\n            end       \n            if senderMarkMobs then \n                if not markerMobs then \n                    change = true\n                elseif senderBuildOption == 2 and markerBuildOption == 1 then \n                    change = true\n                elseif senderLeader and senderBuildOption >= markerBuildOption then \n                    change = true\n                elseif senderRole == \"TANK\" and markerRole ~= senderRole and not markerLeader and senderBuildOption >= markerBuildOption then \n                    change = true \n                elseif senderRole == \"DAMAGER\" and markerRole == \"HEALER\" and not markerLeader and senderBuildOption >= markerBuildOption then \n                    change = true \n                elseif senderRole == markerRole and not markerLeader and senderBuildOption >= markerBuildOption and senderDeciderValue > markerDeciderValue then\n                    change = true \n                end\n            end\n            if aura_env.partyMembers[sender].role ~= senderRole or \n            aura_env.partyMembers[sender].leader ~= senderLeader or \n            aura_env.partyMembers[sender].mobs ~= senderMarkMobs or \n            aura_env.partyMembers[sender].players ~= senderMarkPlayers or \n            aura_env.partyMembers[sender].build ~= senderBuildOption then -- Has any values changed?\n                aura_env.partyMembers[sender].role = senderRole\n                aura_env.partyMembers[sender].mobs = senderMarkMobs\n                aura_env.partyMembers[sender].players = senderMarkPlayers\n                aura_env.partyMembers[sender].leader = senderLeader\n                aura_env.partyMembers[sender].decider = senderDeciderValue\n                aura_env.partyMembers[sender].build = senderBuildOption\n                if sender ~= aura_env.GetPlayerName() then \n                    aura_env.sendPlayerInfo() \n                end \n                -- if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.partyMembers[sender], \"UPDATE_Party[\"..sender..\"]\") end\n            end\n            if change and not aura_env.partyMembers[sender].marker then \n                -- Make old marker inactive\n                if aura_env.partyMembers[marker] then aura_env.partyMembers[marker].marker = false end\n                -- A new player becomes main marker\n                aura_env.partyMembers[sender] = {\n                    [\"marker\"] = true,\n                    [\"role\"] = senderRole,\n                    [\"mobs\"] = senderMarkMobs,\n                    [\"players\"] = senderMarkPlayers,\n                    [\"leader\"] = senderLeader,\n                    [\"decider\"] = senderDeciderValue,\n                    [\"build\"] = senderBuildOption,\n                }\n                -- if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.partyMembers[sender], \"New Marker: [\"..sender..\"]\") end\n                return true\n            end\n            -- if aura_env.config.settings.debug then ViragDevTool_AddData(aura_env.partyMembers, \"aura_env.partyMembers\") end\n        end\n    end\nend\n\n-- CHAT_MSG_ADDON",
