 = next6,\n            },\n            [7] = {\n                [\"mark\"] = mark7,\n                [\"type\"] = type7,\n                [\"next\"] = next7,\n            },\n            [8] = {\n                [\"mark\"] = mark8,\n                [\"type\"] = type8,\n                [\"next\"] = next8,\n            },\n        }\n    else\n        aura_env.mobs[npcId].prio = {\n            [1] = buildOption1,\n            [2] = buildOption2,\n            [3] = buildOption3,\n            [4] = buildOption4,\n        }\n    end\nend\n\n-- AUTOMARK_ADD_MOBS",
