				},
				["init"] = {
					["custom"] = "C_ChatInfo.RegisterAddonMessagePrefix(\"MYTHICAUTOMARK\")\naura_env.rc = LibStub(\"LibRangeCheck-2.0\")\nlocal config = aura_env.config.settings\naura_env.mobs = {}\naura_env.range = {\n    [1] = function(_, guid)\n        for i = 1, 40, 1 do \n            if guid == UnitGUID(\"nameplate\"..i) then return true end\n        end\n    return false end,\n    [2] = function() return true end,\n    [3] = function(r) return r and r<=40 or false end,\n    [4] = function(r) return r and r<=50 or false end,\n    [5] = function(r) return r and r<=60 or false end,\n    [6] = function(r) return r and r<=70 or false end,\n    [7] = function(r) return r and r<=80 or false end,\n    [8] = function(r) return r and r<=90 or false end,\n    [9] = function(r) return r and r<=100 or false end,\n    [10] = function() return false end,\n}\naura_env.marks = {\n    [1] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [2] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [3] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [4] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [5] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [6] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [7] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n    [8] = {\n        [\"active\"] = false, \n        [\"next\"] = {},\n    }, \n}\naura_env.specializationName = {\n    [250] = \"TANK\",\n    [581] = \"TANK\",\n    [104] = \"TANK\",\n    [268] = \"TANK\",\n    [66] = \"TANK\",\n    [73] = \"TANK\",\n    [105] = \"HEALER\",\n    [270] = \"HEALER\",\n    [65] = \"HEALER\",\n    [256] = \"HEALER\",\n    [257] = \"HEALER\",\n    [264] = \"HEALER\",\n    [251] = \"DAMAGER\",\n    [252] = \"DAMAGER\",\n    [577] = \"DAMAGER\",\n    [102] = \"DAMAGER\",\n    [103] = \"DAMAGER\",\n    [253] = \"DAMAGER\",\n    [254] = \"DAMAGER\",\n    [255] = \"DAMAGER\",\n    [62] = \"DAMAGER\",\n    [63] = \"DAMAGER\",\n    [64] = \"DAMAGER\",\n    [269] = \"DAMAGER\",\n    [70] = \"DAMAGER\",\n    [258] = \"DAMAGER\",\n    [259] = \"DAMAGER\",\n    [260] = \"DAMAGER\",\n    [261] = \"DAMAGER\",\n    [262] = \"DAMAGER\",\n    [263] = \"DAMAGER\",\n    [265] = \"DAMAGER\",\n    [266] = \"DAMAGER\",\n    [267] = \"DAMAGER\",\n    [71] = \"DAMAGER\",\n    [72] = \"DAMAGER\",\n}\n\naura_env.GetInstanceID = function()\n    -- Returns a STRING with the Instance ID the player curently is in. \n    return tostring(select(8, GetInstanceInfo())) or \"\" \nend\n\naura_env.GetChatType = function()\n    -- Returns a STRING with the Chat Type for 'SendAddonMessage'\n    return IsInGroup(LE_PARTY_CATEGORY_INSTANCE) and \"INSTANCE_CHAT\" or \"PARTY\"\nend\n\naura_env.GetPlayerName = function()\n    return string.format(\"%s-%s\", UnitFullName(\"player\"))\nend\n\naura_env.playerWantsToMark = function()\n    local role = UnitGroupRolesAssigned(\"player\") == \"NONE\" and aura_env.specializationName[GetSpecializationInfo(GetSpecialization())] or UnitGroupRolesAssigned(\"player\")\n    return config.markerInfo.role == 1 \n    or (config.markerInfo.role == 2 and role == \"TANK\") \n    or (config.markerInfo.role == 3 and UnitIsGroupLeader(\"player\"))\n    or (config.markerInfo.role == 4 and (UnitIsGroupLeader(\"player\") or role == \"TANK\"))\nend\n\naura_env.partyMembers = {\n    [aura_env.GetPlayerName()] = {\n        [\"role\"] = UnitGroupRolesAssigned(\"player\") == \"NONE\" and aura_env.specializationName[GetSpecializationInfo(GetSpecialization())] or UnitGroupRolesAssigned(\"player\"),\n        [\"leader\"] = UnitIsGroupLeader(\"player\"),\n        [\"players\"] = config.playerMarks.tank > 1 or config.playerMarks.healer > 1,\n        [\"decider\"] = C_PlayerInfo.GetPlayerMythicPlusRatingSummary(\"player\").currentSeasonScore,\n        [\"mobs\"] = aura_env.playerWantsToMark(),\n        [\"marker\"] = aura_env.playerWantsToMark(),\n        [\"build\"] = 1,\n    }\n}\n\naura_env.CreateMarkOnUnitId = function(unitId, guid, markID)\n    for _,v in pairs(aura_env.marks) do if v.guid == guid and v.active then return end end -- Mob is alrdy marked\n    local npcID = select(6, strsplit(\"-\", guid))\n    npcID = tonumber(npcID)\n    if UnitGUID(unitId) ~= guid then -- UnitId has been lost, try to restore it. \n        for i = 1, 40, 1 do \n            if guid == UnitGUID(\"nameplate\"..i) then unitId = \"nameplate\"..i break end \n        end\n    end\n    if aura_env.mobs[npcID].build == 1 and not markID then -- Standard Build \n        for i = 1, 4, 1 do -- Check if any Prio marks are set\n            local prio = aura_env.mobs[npcID][\"prio\"][i]\n            if prio < 9 and (not aura_env.marks[prio].active or (aura_env.marks[prio].time and GetTime()-aura_env.marks[prio].time > 60)) then\n                markID = prio\n                break\n            end\n        end\n        if not markID then -- No prio marks, using random marks\n            for i = 1, 8, 1 do\n                -- Getting value from first markOrder option\n                local k = tonumber(9-config.markOptions[\"markOrder\"..i])\n                if (not aura_env.marks[k].active or \n                    (aura_env.marks[k].time and GetTime()-aura_env.marks[k].time > 60)) \n                and config.markOptions[\"toggleMark\"..k] \n                and 10-config.playerMarks.tank ~= k \n                and 10-config.playerMarks.healer ~= k then \n                    markID = k\n                    break\n                end\n            end\n        end\n    end\n    if aura_env.mobs[npcID].build == 2 and not markID then -- Modified Build\n        local mob = aura_env.mobs[npcID]\n        local step = tonumber(mob.step)\n        local mark, type, next = tonumber(mob.prio[step].mark), tonumber(mob.prio[step].type), tonumber(mob.prio[step].next)\n        if type == 1 then markID = mark -- Mark: Force »\n        elseif type == 2 then -- Mark: Use next available from Mark Order »\n            for i = 1, 8, 1 do \n                local k = tonumber(9-config.markOptions[\"markOrder\"..i])\n                -- Getting value from first markOrder option\n                if (not aura_env.marks[k].active \n                    or (aura_env.marks[k].time and GetTime()-aura_env.marks[k].time > 60))\n                and config.markOptions[\"toggleMark\"..k] \n                and 10-config.playerMarks.tank ~= k \n                and 10-config.playerMarks.healer ~= k then \n                    markID = k\n                    break\n                end\n            end\n        elseif type == 3 then -- Mark: If Available »\n            if not aura_env.marks[mark].active then markID = mark end\n        elseif type == 4 then -- Mark: If Available or Add to Queue »\n            if not aura_env.marks[mark].active then -- Mark is not active\n                markID = mark \n            elseif aura_env.marks[mark].active then -- Mark is active, add to Queue\n                local nameplate\n                for i = 1, 40, 1 do \n                    if guid == UnitGUID(\"nameplate\"..i) then \n                        nameplate = \"nameplate\"..i \n                        break\n                    end\n                end\n                if nameplate then \n                    aura_env.marks[mark].next[guid] = {[\"nameplate\"] = nameplate}\n                end\n            end\n        end\n        aura_env.mobs[npcID].step = next\n    end\n    if markID then \n        aura_env.marks[markID].guid, aura_env.marks[markID].time, aura_env.marks[markID].active = guid, GetTime(), true\n        -- if config.debug then ViragDevTool_AddData(aura_env.marks, \"aura_env.marks\") end\n        return SetRaidTarget(unitId, markID), WeakAuras.ScanEvents(\"AUTOMARK_ANNOUNCE\", npcID, markID)\n    end\nend\n\naura_env.updatePlayerMarks = function()\n    local name\n    if UnitFullName(\"player\") then name = string.format(\"%s-%s\", UnitFullName(\"player\")) end \n    if name and aura_env.partyMembers[name].marker and aura_env.partyMembers[name].players then \n        for i = 0, GetNumGroupMembers(), 1 do \n            local pointer = i == 0 and \"player\" or (\"party\"..i)\n            if UnitGroupRolesAssigned(pointer) == \"TANK\" then \n                SetRaidTarget(pointer, 10-config.playerMarks.tank)\n            end\n            if UnitGroupRolesAssigned(pointer) == \"HEALER\" then \n                SetRaidTarget(pointer, 10-config.playerMarks.healer)\n            end\n        end\n    end\nend\n\naura_env.sendPlayerInfo = function()\n    -- ROLE - MarkMobs - MarkPlayers - LEADER - TankIcon - HealerIcon - Decider\n    local role = UnitGroupRolesAssigned(\"player\") == \"NONE\" and aura_env.specializationName[GetSpecializationInfo(GetSpecialization())] or UnitGroupRolesAssigned(\"player\")\n    local leader, score = UnitIsGroupLeader(\"player\"), C_PlayerInfo.GetPlayerMythicPlusRatingSummary(\"player\").currentSeasonScore\n    local markMobs = config.markerInfo.role == 1 or (config.markerInfo.role == 2 and role == \"TANK\") or (config.markerInfo.role == 3 and leader) or (config.markerInfo.role == 4 and (leader or role == \"TANK\"))\n    local markPlayers = (config.playerMarks.tank > 1 or config.playerMarks.healer > 1)\n    local msg = string.format(\"%s:%s:%s:%s:%s:%s\", role, tostring(markMobs), tostring(markPlayers), tostring(leader), score, aura_env.partyMembers[aura_env.GetPlayerName()].build)\n    -- if config.debug then ViragDevTool_AddData(msg, \"SendAddonMessage.WHO_MARK\") end\n    return C_ChatInfo.SendAddonMessage(\"MYTHICAUTOMARK\", \"WHO_MARK^\"..msg, aura_env.GetChatType())\nend\n\naura_env.sendRoleCall = function()\n    -- Send message to others to update\n    return C_ChatInfo.SendAddonMessage(\"MYTHICAUTOMARK\", \"UPDATE_ME^nil\", aura_env.GetChatType())\nend\n\naura_env.convertAddonMsg = function(msg)\n    local t = {}\n    local m = {strsplit(\":\", msg)}\n    for _,v in pairs(m) do \n        if tonumber(v) then v = tonumber(v)\n        elseif v == \"true\" then v = true \n        elseif v == \"false\" then v = false\n        end\n        t[#t+1] = v\n    end\n    return unpack(t)\nend",
