 maximum = 2,\n    short = \"|cffff0066(JC)|r\",\n    long = \"|cffff0066Jewelcrafting|r\"\n  },\n  {name=\"Incandescent Curio\", quests={70520}, optionKey=\"jewelcrafting\", skillID=755,\n    tip = \"loot Elemental Enemies\",\n    maximum = 1,\n    short = \"|cffff0066(JC)|r\",\n    long = \"|cffff0066Jewelcrafting|r\"\n  }, \n  {name=\"Elegantly Engraved..\", quests={70521}, optionKey=\"jewelcrafting\", skillID=755,\n    tip = \"Embellishment loot Nokhud/Sundered Flame Enemies\",\n    maximum = 1,\n    short = \"|cffff0066(JC)|r\",\n    long = \"|cffff0066Jewelcrafting|r\"\n  }, \n  \n  --Leatherworking\n  {name=\"Dirt/Pack\", quests={66384, 66385}, optionKey=\"leatherworking\", skillID=165,\n    tip = \"Dig Disturbed Dirt or Loot Expedition Scout's Pack\",\n    maximum = 2,\n    short = \"|cffffaa00(LW)|r\",\n    long = \"|cffffaa00Leatherworking)|r\"\n  },\n  {name=\"Exceedingly Soft Skin\", quests={70523}, optionKey=\"leatherworking\", skillID=165,\n    tip = \"loot Slyvern/Vorquin Enemies\",\n    maximum = 1,\n    short = \"|cffffaa00(LW)|r\",\n    long = \"|cffffaa00Leatherworking)|r\"\n  }, \n  {name=\"Ossified Hide\", quests={70522}, optionKey=\"leatherworking\", skillID=165,\n    tip = \"Proto-drake or Proto-dragon Enemies\",\n    maximum = 1,\n    short = \"|cffffaa00(LW)|r\",\n    long = \"|cffffaa00Leatherworking)|r\"\n  },  \n  \n  \n  --Tailoring\n  {name=\"Dirt/Pack\", quests={66386, 66387}, optionKey=\"tailoring\", skillID=197,\n    tip = \"Dig Disturbed Dirt or Loot Expedition Scout's Pack\", \n    maximum = 2,\n    short = \"|cff595959(Tail)|r\",\n    long = \"|cff595959Ttailoring)|r\"\n  },\n  {name=\"Stupidly Effective Stitchery\", quests={70525}, optionKey=\"tailoring\", skillID=197,\n    tip = \"loot Gnoll Enemies\",\n    maximum = 1,\n    short = \"|cff595959(Tail)|r\",\n    long = \"|cff595959Ttailoring)|r\"\n  },\n  {name=\"Ohn'arhan Weave\", quests={70524}, optionKey=\"tailoring\", skillID=197,\n    tip = \"loot Nokhud Enemies\",\n    maximum = 1,\n    short = \"|cff595959(Tail)|r\",\n    long = \"|cff595959Ttailoring)|r\"\n  },\n  \n  \n  --Herbalism\n  {name=\"Dreambloom\", quests={71857, 71858, 71859, 71860, 71861, 71864}, optionKey=\"herbalism\", skillID=182,\n    tip = \"Any herbs\",\n    maximum = 6,\n    short = \"|cff00ff59(Herb)|r\",\n    long = \"|cff00ff59Herbalism|r\"\n  },\n  \n  \n  --Mining\n  {name = \"Iridescent Ore\", quests= {72160,72161,72162,72163,72164,72165},optionKey = \"mining\",skillID=186,\n    tip = \"Any Mining\",\n    maximum = 6,\n    short = \"|cffff6633(Mine)|r\",\n    long = \"|cffff6633Mining|r\",\n  },\n  \n  \n  --Skinning\n  {name=\"Curious Hide\", quests={70381, 70383, 70384, 70385, 70386, 70389}, optionKey=\"skinning\", skillID=393,\n    tip=\"Any Skining\",\n    maximum = 6,\n    short = \"|cff884422(SK)|r\",\n    long = \"|cff884422Skinning|r\"\n  },\n  \n}\n\n-- This returns true if at least one of the required quests is currently active.\nlocal is_active = function(objective)\n  if not aura_env.config[objective.optionKey] then\n    return false\n  end\n  if not objective.required_quests then\n    return true\n  end\n  for _, q in ipairs(objective.required_quests) do\n    if C_TaskQuest.GetQuestTimeLeftSeconds(q) or C_QuestLog.IsQuestFlaggedCompleted(q) then\n      return true\n    end\n  end\nend\n\n-- This returns the number of quests that are not complete.\nlocal remaining_quests = function(objective)\n  local c = #objective.quests\n  for _, q in ipairs(objective.quests) do\n    if C_QuestLog.IsQuestFlaggedCompleted(q) then\n      c = c - 1\n    end\n  end\n  return c\nend\n\nlocal profession_known = function(objective)\n  local prof1, prof2 = GetProfessions()\n  local prof1SkillID, prof2SkillID, _\n  if (prof1 ~= nil) then\n    _,_,_,_,_,_,prof1SkillID = GetProfessionInfo(prof1)\n  end\n  if (prof2 ~= nil) then\n    _,_,_,_,_,_,prof2SkillID = GetProfessionInfo(prof2)\n  end\n  if (objective.skillID == prof1SkillID) or (objective.skillID == prof2SkillID) then\n    return true\n  else\n    if not aura_env.config[\"automaticDetection\"] then\n      return true\n    else\n      return false\n    end\n  end\nend\nif aura_env.config.Extra and aura_env.config.Show then\n  aura_env.update_display = function()\n    aura_env.text = \"|cff00ffaaProfession Knowledge Weeklies|r\"\n    for _, objective in ipairs(objectives) do\n      if is_active(objective) and profession_known(objective) then\n        local c = remaining_quests(objective)\n        if c > 0 then\n          if aura_env.text then\n            aura_env.text = aura_env.text ..  \"\\n\" ..objective.long..\" - \"..objective.name .. \" = \" .. c ..\" remaining\"..\" Knowledge by \"..objective.tip ..\" \" \n          else\n            aura_env.text = objective.long..\" - \"..objective.name .. \" = \" .. c ..\" remaining\"..\" Knowledge by \"..objective.tip ..\" \" \n          end\n        end\n      end\n    end\n  end\n  if aura_env.text == \"|cff00ffaaProfession Knowledge Weeklies|r\" then \n    aura_env.update_display = function()\n      aura_env.text = \"\"\n    end\n  end \nelseif aura_env.config.Show then\n  aura_env.update_display = function()\n    aura_env.text = \"|cff00ffaaProfession Knowledge Weeklies|r\"\n    for _, objective in ipairs(objectives) do\n      if is_active(objective) and profession_known(objective) then\n        local c = remaining_quests(objective)\n        if c > 0 then\n          if aura_env.text then\n            aura_env.text = aura_env.text .. \"\\n\" ..objective.short..\"-\"..objective.name .. \": \" .. c ..\" remaining\"\n          else\n            aura_env.text = objective.short..\"-\"..objective.name .. \": \" .. c  ..\" remaining\"\n          end\n        end\n      end\n    end\n  end\n  if aura_env.text == \"|cff00ffaaProfession Knowledge Weeklies|r\" then \n    aura_env.update_display = function()\n      aura_env.text = \"\"\n    end\n  end \nelse \n  aura_env.update_display = function()\n    aura_env.text = nil\n  end\nend\n\n\n