			["preferToUpdate"] = false,
			["customText"] = "function()\n    local s = aura_env.states[1]\n    return not s.connected and \"OFFLINE\"\n    or not s.visible and \"FAR\"\n    or s.dead and \"DEAD\"\n    \n    or format(\"%s%%\", s.percent)\nend",
