						["events"] = "TRIGGER:2:3 FRAME_UPDATE GROUP_ROSTER_UPDATE ROLE_CHANGED_INFORM PLAYER_ENTERING_WORLD",
						["custom"] = "function(states, event, triggerNum, triggerStates)\n    local cfg = aura_env.config\n    \n    if event == \"GROUP_ROSTER_UPDATE\"\n    or event == \"ROLE_CHANGED_INFORM\"\n    or event == \"PLAYER_ENTERING_WORLD\"\n    then\n        for _, v in pairs(states) do\n            v.show = false\n            v.changed = true\n        end\n        \n        for unit in WA_IterateGroupMembers() do\n            if UnitGroupRolesAssigned(unit) == \"HEALER\" then\n                local _, class = UnitClass(unit)\n                local icon = aura_env.classIcons[class]\n                if icon then\n                    local value = UnitPower(unit, Enum.PowerType.Mana)\n                    local total = UnitPowerMax(unit, Enum.PowerType.Mana)\n                    states[unit] = {\n                        icon = icon,\n                        unit = unit,\n                        value = value,\n                        total = total,\n                        percent = Round(value/total*100),\n                        class = class,\n                        \n                        connected = UnitIsConnected(unit),\n                        visible = UnitIsVisible(unit),\n                        dead = UnitIsDeadOrGhost(unit),\n                        \n                        inRange = WeakAuras.CheckRange(unit, 40, \"<=\"),\n                        drinking = WA_GetUnitAura(unit, GetSpellInfo(257427), \"HELPFUL\"),\n                        \n                        duration = 0,\n                        expirationTime = 0,\n                        progressType = \"timed\",\n                        changed = true,\n                        show = true,\n                    }\n                end\n            end\n        end\n        return true\n    end\n    \n    if event == \"TRIGGER\" and triggerNum and triggerStates then\n        if triggerNum == 2 then --power trigger\n            local changed = false\n            \n            for _, v in pairs(triggerStates) do\n                local state = v.unit and states[v.unit]\n                if state then\n                    state.value = v.value == 0 and state.value or v.value\n                    state.total = v.total == 0 and state.total or v.total\n                    state.percent = v.value == 0 and Round(state.percent) or Round(v.percentpower)\n                    state.changed = true\n                    \n                    changed = true\n                end\n            end\n            \n            return changed\n        elseif triggerNum == 3 then --buff trigger\n            for _, v in pairs(states) do\n                local icon = aura_env.classIcons[v.class]\n                if icon then\n                    v.icon = icon\n                    v.duration = 0\n                    v.expirationTime = 0\n                    v.drinking = false\n                    v.changed = true\n                end\n            end\n            \n            for _, v in pairs(triggerStates) do\n                local state = v.unit and states[v.unit]\n                if state then\n                    state.icon = v.icon\n                    state.duration = v.duration\n                    state.expirationTime = v.expirationTime\n                    state.drinking = v.duration == 30 or v.duration == 20 or v.duration == 15\n                    state.changed = true\n                end\n            end\n            \n            return true\n        end\n    elseif event == \"FRAME_UPDATE\" then\n        local theTime = GetTime()\n        if not aura_env.last or aura_env.last < theTime - cfg.throttle then\n            aura_env.last = theTime\n            \n            local changed = false\n            local connected = false\n            local visible = false\n            local inRange = false\n            local dead = false\n            \n            for _, state in pairs(states) do\n                if state.unit then\n                    connected = UnitIsConnected(state.unit)\n                    if state.connected ~= connected then\n                        state.connected = connected\n                        changed = true\n                    end\n                    if connected then\n                        visible = UnitIsVisible(state.unit)\n                        if state.visible ~= visible then\n                            state.visible = visible\n                            changed = true\n                        end\n                        dead = UnitIsDeadOrGhost(state.unit)\n                        if state.dead ~= dead then\n                            state.dead = dead\n                            changed = true\n                        end\n                        if visible then\n                            inRange = WeakAuras.CheckRange(state.unit, 40, \"<=\")\n                            if state.inRange ~= inRange then\n                                state.inRange = inRange\n                                changed = true\n                            end\n                        end\n                    end\n                    \n                    state.changed = changed\n                end\n            end\n            return changed\n        end\n    end\nend",
