  {questId = 71033,}, --Raging Torrent mobID 197221 hidden ID 71033 --\n    },\n  },\n  {name = \"elements\",maximum = 1,\n    quests = {\n      {questId = 71995,}, --Therrocite mob id 197749--\n    },\n  },\n  { name = \"worldBoss\",  maximum = 1,\n    quests = {\n      {questId = 69927,}, -- Bazual mobID 193532, hidden ID####--\n      {questId = 69928,},-- Liskanoth  mobID 193533, hidden ID####--\n      {questId = 69929,},-- Strunraan mobID 193534, hidden ID####--\n      {questId = 69930,},-- Basrikron mobID 193535, hidden ID####--\n      \n    },\n  },\n  {name = \"Chests\",maximum = 1,\n    quests = {\n      {questId = 72376,}, --Supply Chest (warmode)\n    },\n  },\n  {name = \"SparksOfLife\", maximum = 1,\n    quests = {\n      {questId = 72646,},--Sparks of Life: The Waking Shores\n      {questId = 72647,},--Sparks of Life: Ohn'ahran Plains\n      {questId = 72648,},--Sparks of Life: The Azure Span\n      {questId = 72649,},--Sparks of Life: Thaldraszus\n    },\n  },\n  \n  \n}\n\n--Output Varible--\naura_env.text = '';\n--Output Main function--\naura_env.update_overview_display = function()\n  local text = ''\n  --Include switchesCatalyst\n  local todov = aura_env.config.TD.showOverview\n  local wbv = aura_env.config.TD.includeWorldBoss\n  local Cfv = aura_env.config.TD.includeCommunityFeast\n  local elev = aura_env.config.TD.includeelements\n  local floodv = aura_env.config.TD.includeFlood\n  local SoDKv = aura_env.config.TD.includeSoDK\n  local huntv = aura_env.config.TD.includeHunt\n  local Catav = aura_env.config.TD.includeCatalyst\n  local Warv = aura_env.config.TD.includeWar\n  local WQv = aura_env.config.TD.includeWeeklyQuest\n  --Hide switches\n  local wbh = aura_env.config.TD.hide\n  local Cfh = aura_env.config.TD.hide\n  local eleh = aura_env.config.TD.hide\n  local floodh = aura_env.config.TD.hide\n  local SoDKh = aura_env.config.TD.hide\n  local hunth = aura_env.config.TD.hide\n  local WQh = aura_env.config.TD.hide\n  local Warh = aura_env.config.TD.hide\n  local Catah = aura_env.config.TD.hide\n  local tracking = 0\n  -----------------------------------------------------Todo list Prep--------------------------------------------------------------------------- \n  for _, todoEntry in ipairs(aura_env.todoList) do\n    -- PINK --\n    local entry, color, completed, maximum = '', 'ffff00ff', 0, #todoEntry.quests\n    if \n    --set up the items to displaySparksOfLife\n    todoEntry.name == 'SparksOfLife' and Warv or--Sparks of Life (warmode)--\n    todoEntry.name == 'Chests' and Warv or--Supply Chest (warmode)--\n    todoEntry.name == 'worldBoss' and wbv or -- world boss\n    todoEntry.name == 'CommunityFeast' and Cfv or-- Tuskarr Community Feast--\n    todoEntry.name == 'elements' and elev or--Trial of Elements --\n    todoEntry.name == 'flood' and floodv or--Trial of Floods --\n    todoEntry.name == 'SoDK' and SoDKv or--Siege of Dragonscale Keep --\n    todoEntry.name == 'Hunt' and huntv or--Grand Hunts\n    todoEntry.name == 'Catalyst' and Catav or--Catalyst\n    todoEntry.name == 'weeklyQuest' and WQv then--Aiding the Accord--\n      if todoEntry.maximum then\n        maximum = todoEntry.maximum\n      end\n      for _, todoQuest in ipairs(todoEntry.quests) do\n        if todoEntry.name == 'worldBoss' then\n          if aura_env.is_quest_completed(todoQuest.questId) then\n            completed = completed + 1\n            tracking = tracking +1\n          end\n        else\n          if aura_env.is_quest_completed(todoQuest.questId) then\n            completed = completed + 1\n            tracking = tracking +1\n          end\n        end\n      end\n      if (maximum > 1) and (completed >= (maximum / 2)) then\n        -- Burnt Orange --\n        color = 'ffff7801'\n      end \n      if completed >= maximum then\n        -- JADE--\n        color = 'FF00ff96'\n      end\n      \n      if completed >= maximum then\n        if\n        --check if the use wants to hide the line after complete\n        todoEntry.name == 'SparksOfLife' and Warv or--Sparks of Life (warmode)--\n        todoEntry.name == 'Chests' and not Warh  or--Supply Chest (warmode)--\n        todoEntry.name == 'worldBoss' and not wbh or-- world boss\n        todoEntry.name == 'CommunityFeast' and not Cfh or -- Tuskarr Community Feast--\n        todoEntry.name == 'elements' and not eleh or --Trial of Elements --\n        todoEntry.name == 'flood' and not floodh or --Trial of Floods --\n        todoEntry.name == 'SoDK' and not SoDKh or--Siege of Dragonscale Keep --\n        todoEntry.name == 'Hunt' and not hunth or --Grand Hunts --\n        todoEntry.name == 'Catalyst' and not Catah or--Catalyst\n        todoEntry.name == 'weeklyQuest' and not WQh then --Aiding the Accord--\n          \n          entry ='    ' ..  aura_env.get_text_snippet(todoEntry.name) .. ' ' .. WrapTextInColorCode(completed .. ' / ' .. maximum, color) .. '\\r\\n'\n        end\n      else\n        entry ='    ' .. aura_env.get_text_snippet(todoEntry.name) .. ' ' .. WrapTextInColorCode(completed .. ' / ' .. maximum, color) .. '\\r\\n'\n      end\n      \n      text = text .. entry\n    end\n  end\n  \n  return text\nend\n\n--Update output varible--\naura_env.update_display = function()\n  local text = ''\n  ---todo list items update---\n  if aura_env.config.TD.showOverview then -- cheack if use want to show\n    -- if tracking <5 then\n    text ='  ' .. text .. WrapTextInColorCode(aura_env.get_text_snippet('overviewHeadline'), 'ff00ff96') .. '\\r\\n'--apped header\n    --   end\n    text = text .. aura_env.update_overview_display() .. '\\r' --append todo list items\n  end \n  return text\nend\naura_env.text = aura_env.update_display()",
