						["custom"] = "function(event, instanceId)\n    if event == \"AUTOMARK_GET_MOBS\" then \n        if not instanceId == tostring(select(8, GetInstanceInfo()) or C_MythicPlus.GetCurrentSeason() == instanceId) then return end -- Player is not in supported dungeon\n        for _, npc in pairs(aura_env.config.mobs) do \n            npc.npcID = npc.npcID:gsub(\"%s+\", \"\") -- remove spaces\n            for _, id in pairs({ strsplit(\";\", npc.npcID) }) do \n                if npc.autoMarkOption > 1 then \n                    local buildOption1, buildOption2, buildOption3, buildOption4, buildOption5, buildOption6, buildOption7, buildOption8\n                    if npc.build == 1 then \n                        buildOption1 = 10-npc[\"1\"].P1\n                        buildOption2 = 10-npc[\"1\"].P2\n                        buildOption3 = 10-npc[\"1\"].P3\n                        buildOption4 = 10-npc[\"1\"].P4\n                    elseif npc.build == 2 then \n                        buildOption1 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P1, npc[\"2\"].T1-1, npc[\"2\"].G1-1)\n                        buildOption2 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P2, npc[\"2\"].T2-1, npc[\"2\"].G2-1)\n                        buildOption3 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P3, npc[\"2\"].T3-1, npc[\"2\"].G3-1)\n                        buildOption4 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P4, npc[\"2\"].T4-1, npc[\"2\"].G4-1)\n                        buildOption5 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P5, npc[\"2\"].T5-1, npc[\"2\"].G5-1)\n                        buildOption6 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P6, npc[\"2\"].T6-1, npc[\"2\"].G6-1)\n                        buildOption7 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P7, npc[\"2\"].T7-1, npc[\"2\"].G7-1)\n                        buildOption8 = string.format(\"%s:%s:%s\", 10-npc[\"2\"].P8, npc[\"2\"].T8-1, npc[\"2\"].G8-1)\n                    end\n                    WeakAuras.ScanEvents(\"AU