						["custom"] = "function(a, event, ...)\n    local warn = aura_env.config.duration or 15\n    -- ERTNOTE_PHASE\n    if event == \"ERTNOTE_PHASE\"\n    and aura_env.Note\n    and (aura_env.config.enableWNoteDisabled or aura_env.Note.enabled)\n    and ...\n    then\n        local phase = ...\n        aura_env.phase = phase\n        if type(aura_env.phases[phase]) == \"table\" then\n            for start, line in pairs(aura_env.phases[phase]) do\n                local encounterStarted = aura_env.encounterStarted\n                local aura_env = aura_env\n                aura_env.timers[start] = C_Timer.NewTimer(\n                    start < 0 and 0.1 or start,\n                    function()\n                        WeakAuras.ScanEvents(\"ERTPEWPEW\", start, line, \"timed\", tostring(start)..tostring(phase), encounterStarted, phase, aura_env.id)\n                    end\n                )\n            end\n        end\n        -- init\n    elseif event == \"ENCOUNTER_START\"\n    and aura_env.Note\n    and (aura_env.config.enableWNoteDisabled or aura_env.Note.enabled)\n    and ... then\n        aura_env.checks = {}\n        aura_env.counter = {}\n        aura_env.timers = {}\n        aura_env.encounterStarted = GetTime()\n        aura_env.phase = 1\n        aura_env.phases = {}\n        \n        local notes = {\"\", \"\"}\n        if aura_env.Note.Text1 and aura_env.config.sharedEnable then\n            notes[1] = aura_env.Note.Text1\n        end\n        if aura_env.Note.SelfText and aura_env.config.personalEnable then\n            notes[2] = aura_env.Note.SelfText\n        end\n        \n        for i, text in ipairs(notes) do\n            for line in text:gmatch('[^\\r\\n]+') do\n                local keywordFound = false\n                for k in pairs(aura_env.keyword) do\n                    if line:lower():match(k:lower()) then\n                        keywordFound = true\n                        break\n                    end\n                end\n                local ignoreKeywordFound = false\n                for k in pairs(aura_env.ignoreKeyword) do\n                    if line:lower():match(k:lower()) then\n                        ignoreKeywordFound = true\n                        break\n                    end\n                end\n                if (not aura_env.config.filterNote\n                    or line:match(WeakAuras.me)\n                    or keywordFound\n                ) and not ignoreKeywordFound\n                then\n                    local mn, sec, phase = line:match(\"{time:(%d+):(%d+),p(%d+)}\")\n                    if mn == nil and sec == nil then\n                        mn, sec = line:match(\"{time:(%d+):?(%d*)}\")\n                        if mn or sec then\n                            if sec == nil or sec == \"\" then\n                                sec = tonumber(mn)\n                                mn = 0\n                            else\n                                sec = tonumber(sec)\n                                mn = tonumber(mn)    \n                            end\n                        end\n                    end\n                    if mn and sec then\n                        mn = tonumber(mn)\n                        sec = tonumber(sec)\n                        phase = tonumber(phase)\n                        local start = sec + (mn * 60) - warn\n                        if phase == nil or phase == 1 then\n                            local encounterStarted = aura_env.encounterStarted\n                            local aura_env = aura_env\n                            -- schedule timers\n                            aura_env.timers[start] = C_Timer.NewTimer(\n                                start < 0 and 0.1 or start,\n                                function()\n                                    WeakAuras.ScanEvents(\"ERTPEWPEW\", start, aura_env.format(line), \"timed\", start, encounterStarted, nil, aura_env.id)\n                                end\n                            )\n                        elseif phase and phase > 1 then\n                            -- save timers for later phases\n                            aura_env.phases[phase] = aura_env.phases[phase] or {}\n                            aura_env.phases[phase][start] = aura_env.format(line)\n                        end\n                    else\n                        -- save BW & DBM Timers in aura_env.checks\n                        local count, spellId = line:match(\"^[<(](%d+)[>)].-{spell:(%d+)}\")\n                        if count and spellId then\n                            tinsert(aura_env.checks, { spellId = spellId, count = tonumber(count), line = aura_env.format(line) })\n                        end\n                    end\n                end\n            end\n        end\n        \n        -- init bars already shown\n        if aura_env.config.bw then\n            for _, bar in pairs(WeakAuras.GetAllBigWigsTimers()) do\n                aura_env.newBar(bar, \"bw\")\n            end\n        end\n        if aura_env.config.dbm then\n            for _, bar in pairs(WeakAuras.GetAllDBMTimers()) do\n                aura_env.newBar(bar, \"dbm\")\n            end\n        end\n    elseif event == \"ERTPEWPEW\" then\n        local start, line, type, id, encounterStarted, phase, aura_id = ...\n        if encounterStarted == aura_env.encounterStarted\n        and aura_id == aura_env.id\n        and (phase == nil or phase == aura_env.phase)\n        then\n            if start then\n                if start < 0 then\n                    warn = warn + start\n                end\n                a[id] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    autoHide = true,\n                    duration = aura_env.config.duration or 15,\n                    expirationTime = GetTime() + warn,\n                    name = line,\n                    type = type\n                }\n            end\n        end\n    elseif event == \"BigWigs_StartBar\" or event == \"DBM_TimerStart\" then\n        local id = ...\n        if id and aura_env.checks then\n            local bar\n            if event == \"BigWigs_StartBar\" and aura_env.config.bw then\n                bar = WeakAuras.GetBigWigsTimerById(id)\n            elseif aura_env.config.dbm  then\n                bar = WeakAuras.GetDBMTimerById(id)\n            end\n            aura_env.newBar(bar, event == \"BigWigs_StartBar\" and \"bw\" or \"dbm\")\n        end\n    elseif event == \"BigWigs_StopBar\" or event == \"DBM_TimerStop\" then\n        local id = ...\n        if id and aura_env.checks then\n            local bar\n            if event == \"BigWigs_StopBar\" and aura_env.config.bw then\n                bar = WeakAuras.GetBigWigsTimerById(id)\n            elseif aura_env.config.dbm then\n                bar = WeakAuras.GetDBMTimerById(id)\n            end\n            if bar then\n                local s = a[bar]\n                if s then\n                    s.show = false\n                    s.changed = true\n                end\n                if aura_env.timers and aura_env.timers[bar] then\n                    aura_env.timers[bar]:Cancel()\n                    aura_env.timers[bar] = nil\n                end\n                local spellId = bar.spellId\n                if spellId and aura_env.counter and aura_env.counter[spellId] then\n                    aura_env.counter[spellId] = aura_env.counter[spellId] - 1\n                end\n            end\n        end\n    elseif event == \"DBM_TimerPause\" or event == \"BigWigs_PauseBar\" then\n        local id = ...\n        if id and aura_env.checks then\n            local bar\n            if event == \"BigWigs_PauseBar\" and aura_env.config.bw then\n                bar = WeakAuras.GetBigWigsTimerById(id)\n            elseif aura_env.config.dbm then\n                bar = WeakAuras.GetDBMTimerById(id)\n            end\n            if bar then\n                local s = a[bar]\n                if s then\n                    s.paused = true\n                    s.changed = true\n                end\n                if aura_env.timers and aura_env.timers[bar] then\n                    aura_env.timers[bar]:Cancel()\n                    aura_env.timers[bar] = nil\n                end\n            end\n        end\n    elseif event == \"DBM_TimerResume\" or event == \"BigWigs_ResumeBar\" then\n        local id = ...\n        if id and aura_env.checks then\n            local bar\n            if event == \"BigWigs_ResumeBar\" and aura_env.config.bw then\n                bar = WeakAuras.GetBigWigsTimerById(id)\n            elseif aura_env.config.dbm then\n                bar = WeakAuras.GetDBMTimerById(id)\n            end\n            if bar then\n                local s = a[bar]\n                if s then\n                    if s.paused then\n                        s.paused = false\n                        s.changed = true\n                    end\n                else\n                    aura_env.newBar(bar, event == \"BigWigs_ResumeBar\" and \"bw\" or \"dbm\")\n                end\n            end\n        end\n    elseif event == \"DBM_TimerStopAll\" and aura_env.config.dbm then\n        for _, v in pairs(a) do\n            if v.type == \"dbm\" then\n                v.show = false\n                v.changed = true\n            end\n        end\n    elseif event == \"BigWigs_StopBars\" and aura_env.config.bw then\n        for _, v in pairs(a) do\n            if v.type == \"bw\" then\n                v.show = false\n                v.changed = true\n            end\n        end\n    elseif event == \"ENCOUNTER_END\" then\n        aura_env.checks = {}\n        aura_env.counter = {}\n        for _, v in pairs(a) do\n            v.show = false\n            v.changed = true\n        end\n        if aura_env.timers then\n            for k, v in pairs(aura_env.timers) do\n                v:Cancel() \n                aura_env.timers[k] = nil\n            end\n        end\n    end\n    return true\nend",
