						["custom"] = "function(states, ...)    \n    local playerZone = C_Map.GetBestMapForUnit(\"player\")\n    local _, _, classIndex = UnitClass(\"player\")\n    \n    -- rares\n    for _,rare in ipairs(aura_env.config.rares) do\n        if rare.alwaysDisplay or rare.classDisplay[classIndex] then            \n            local isCompleted = C_QuestLog.IsQuestFlaggedCompleted(rare.hiddenQuestId)\n            local skip = (aura_env.config.general.hideCompleted and isCompleted) or (aura_env.config.general.onlyElites and not rare.isElite)\n            \n            if skip then\n                states[rare.hiddenQuestId] = {}\n            else\n                local inZone = true\n                local index = 0\n                local rareZone = aura_env.zones[rare.zone]\n                \n                if rareZone.zoneId ~= playerZone then\n                    index = index + 4\n                    inZone = false\n                end\n                \n                index = index + rare.zone\n                \n                states[rare.hiddenQuestId] = {\n                    show = true,\n                    disp = rare.name,\n                    zone = rareZone[\"zone\"],\n                    inZone = inZone,\n                    isCompleted = isCompleted,\n                    drops = rare.drops,\n                    \n                    index = index\n                }                \n            end            \n        end\n    end\n    \n    -- weekly events\n    if aura_env.config.general.showWeekly then\n        for _,weekly in ipairs(aura_env.config.weeklies) do\n            if weekly.display then\n                local isCompleted = C_QuestLog.IsQuestFlaggedCompleted(weekly.hiddenQuestId)\n                local skip = (aura_env.config.general.hideCompleted and isCompleted)\n                \n                if skip then\n                    states[weekly.hiddenQuestId] = {}\n                else\n                    local inZone = true\n                    local index = 8\n                    local weeklyZone = aura_env.zones[weekly.zone]\n                    local zoneName = weeklyZone[\"zone\"]\n                    \n                    if weeklyZone.zoneId ~= playerZone then\n                        index = index + 4\n                        inZone = false\n                    end \n                    \n                    -- if weekly is in multiple zones, e.g. Great Hunt, hide zone\n                    if weekly.multipleZones then\n                        zoneName = \"\"\n                        inZone = false\n                        index = index + 4\n                    end\n                    \n                    states[weekly.hiddenQuestId] = {\n                        show = true,\n                        disp = weekly.name,\n                        zone = zoneName,\n                        inZone = inZone,\n                        isCompleted = isCompleted,\n                        \n                        index = index\n                    }                \n                end            \n            end\n        end\n    end\n    \n    return true\nend",
